INSERT INTO `armed_forces_law` (`Id`, `Section`, `Sub-section`, `Details`) VALUES
(1, 'সংজ্ঞা-ধারা ২', 'উপ-ধারা (১) ', 'বিষয় বা প্রসঙ্গের পরিপন্থী কোনো কিছু না থাকিলে, এই অধ্যাদেশে-\r\nদফা (জজ) \"র‍্যাপিড অ্যাকশন ব্যাটালিয়ন\" অর্থ এই অধ্যাদেশের অধীনে প্রতিষ্ঠিত একটি র‍্যাপিড অ্যাকশন ব্যাটালিয়ন;\r\nদফা (ঙ) “বাহিনী” অর্থ ধারা ৩-এর অধীন গঠিত সশস্ত্র পুলিশ বাহিনী;\r\n'),
(2, 'বাহিনী গঠন এবং ব্যবস্থাপনা–ধারা ৩', 'উপ-ধারা (১) ', 'এই অধ্যাদেশের বিধান অনুসারে সশস্ত্র পুলিশ বাহিনী নামে একটি বাহিনী গঠন করা হইবে এবং ব্যবস্থাপনা করা হইবে।'),
(3, 'বাহিনী গঠন এবং ব্যবস্থাপনা–ধারা ৩', 'উপ-ধারা (৩)', 'বাহিনীতে এক বা একাধিক র‍্যাপিড অ্যাকশন ব্যাটালিয়ন অন্তর্ভুক্ত থাকিবে।\r\n'),
(4, 'বাহিনীর দায়িত্ব–ধারা ৬', '', 'এই বাহিনী নিম্নলিখিত কার্য সম্পাদনের উদ্দেশ্যে নিয়োজিত হইবে, যথা :\r\nদফা(ক) অভ্যন্তরীণ নিরাপত্তার দায়িত্ব;\r\nদফা(কক) অপরাধ ও অপরাধমূলক কর্মকাণ্ড সম্পর্কিত গোয়েন্দাগিরি;\r\nদফা(খ) সরকারের সময়ে সময়ে প্রদত্ত নির্দেশ অনুযায়ী অবৈধ অস্ত্র, গোলাবারুদ, বিস্ফোরক এবং এই জাতীয় দ্রব্য পুনরুদ্ধার;\r\nদফা(খখ) সরকারের নির্দেশ মতে কোনো অপরাধের তদন্ত সম্পাদন;\r\nদফা(গ) সশস্ত্র অপরাধীচক্র গ্রেফতার;\r\nদফা(ঘ) পুলিশসহ অন্যান্য আইন প্রয়োগকারী সংস্থাগুলিকে আইন-শৃঙ্খলা রক্ষার জন্য সহায়তা করা; এবং\r\nদফা(ঙ) সরকার কর্তৃক সময়ে সময়ে প্রদত্ত অন্যান্য দায়িত্ব পালন।\r\n\r\n'),
(5, 'র‍্যাপিড অ্যাকশন ব্যাটালিয়নের দায়িত্ব-ধারা ৬ক', '', 'র‍্যাপিড অ্যাকশন ব্যাটালিয়ন বাহিনীর অন্যান্য ব্যাটালিয়ানের সাথে ধারা ৬-এ উল্লিখিত সকল দায়িত্ব পালন করিবে, তবে র‍্যাপিড অ্যাকশন ব্যাটালিয়ন ব্যতীত অন্য কোনো ব্যাটালিয়ন দফা (কক) এবং (খখ)-এ উল্লিখিত কোনো দায়িত্ব পালন করিবে না।\r\n'),
(6, 'অপরাধ তদন্তে র‍্যাপিড অ্যাকশন ব্যাটালিয়নকে নির্দেশ প্রদানের ক্ষমতা-ধারা ৬খ', 'উপ-ধারা (১)', 'সরকার, যে কোনো সময় যে কোনো অপরাধ তদন্তের জন্য র‍্যাপিড অ্যাকশন ব্যাটালিয়নকে নির্দেশ প্রদান করিতে পারে।'),
(7, 'অপরাধ তদন্তে র‍্যাপিড অ্যাকশন ব্যাটালিয়নকে নির্দেশ প্রদানের ক্ষমতা-ধারা ৬খ', 'উপ-ধারা (২)', 'উপ-ধারা (১) এর অধীনে কোনো নির্দেশ জারি করা হইলে সংশ্লিষ্ট র‍্যাপিড অ্যাকশন ব্যাটালিয়নের কমান্ডিং অফিসার এইরূপ অপরাধের তদন্তের নিয়ন্ত্রণ গ্রহণ করিবেন।\r\n'),
(8, 'অপরাধ তদন্তে র‍্যাপিড অ্যাকশন ব্যাটালিয়নকে নির্দেশ প্রদানের ক্ষমতা-ধারা ৬খ', 'উপ-ধারা (৩)', 'একটি র‍্যাপিড অ্যাকশন ব্যাটালিয়নের কমান্ডিং অফিসার নিজেই এইরূপ কোনো অপরাধ তদন্ত করিতে পারেন বা অপরাধ তদন্তের জন্য তাহার অধস্তন কর্মকর্তাকে নির্দেশ দিতে পারেন।\r\n'),
(9, 'অপরাধের তদন্তের জন্য অনুসরণীয় পদ্ধতি-ধারা ৬গ\r\n', 'উপ-ধারা (১)', 'র‍্যাপিড অ্যাকশন ব্যাটালিয়নের কোনো কর্মকর্তা কোনো অপরাধ তদন্তের জন্য ফৌজদারী কার্যবিধি, ১৮৯৮ (১৮৯৮ সালের ৫নং আইন) বা অন্য কোনো আইনে, যাহা প্রযোজ্য নির্ধারিত পদ্ধতি অনুসরণ করিবেন।'),
(10, 'অপরাধের তদন্তের জন্য অনুসরণীয় পদ্ধতি-ধারা ৬গ', 'উপ-ধারা (২) ', 'ফৌজদারী কার্যবিধি, ১৮৯৮ (১৮৯৮ ৫নং আইন) বা আপাতত বলবৎ অন্য কোনো আইনে যাহা কিছুই বলা হউক না কেনো, সংশ্লিষ্ট কর্মকর্তা এই অধ্যাদেশের অধীনে কোনো অপরাধের তদন্ত সম্পন্ন করিবার পর তাহার প্রতিবেদন সংশ্লিষ্ট থানার ভারপ্রাপ্ত কর্মকর্তার নিকট দাখিল করিবেন, এবং থানার ভারপ্রাপ্ত কর্মকর্তা এইরূপ প্রতিবেদন প্রাপ্তির আটচল্লিশ ঘন্টার মধ্যে উপযুক্ত আদালত বা প্রযোজ্য ক্ষেত্রে ট্রাইব্যুনালে প্রেরণ করিবেন।\r\n'),
(11, 'কোনো অপরাধ তদন্তের ক্ষেত্রে কর্মকর্তার ক্ষমতা–ধারা ৬ঘ', '', 'কোনো র‍্যাপিড অ্যাকশন ব্যাটালিয়নের যে কোনো কর্মকর্তা এই অধ্যাদেশের আওতায় কোনো অপরাধ তদন্তকালে বা কোনো দায়িত্ব পালন কালে, উক্ত অপরাধ হইতে উদ্ভূত মামলায়, সেইরূপ ক্ষমতা প্রয়োগ এবং কর্তব্য এবং দায়িত্ব সম্পাদন করিতে পারিবেন, একজন পুলিশ অফিসার ফৌজদারী কার্যবিধি, ১৮৯৮ (১৮৯৮ ৫নং আইন)-এর আওতায় যেইরূপ ক্ষমতা প্রয়োগ করিতে বা যেইরূপ কর্তব্য এবং দায়িত্ব সম্পাদন করিতে পারেন।\r\n\r\n');
INSERT INTO `border_guard_bangladesh_laws` (`Id`, `Section`, `Sub-section`, `Details`) VALUES
(1, 'বাহিনীর কার্যাবলী–ধারা ১১(১)', '', 'বন্যপ্রাণী অবৈধ ব্যবসা দমনের জন্য প্রযোজ্য বর্ডার গার্ড বাংলাদেশ-এর কার্যাবলী ঃ\r\n(ক) সক্রিয় কর্তব্য হিসাবে সীমান্তের নিরাপত্তা রক্ষা করা;\r\n(খ) চোরাচালান, নারী ও শিশু এবং মাদকদ্রব্য পাচার সংক্রান্ত অপরাধসহ অন্যান্য আঙ্গুরাষ্ট্র সীমান্ত অপরাধ প্রতিরোধ করা;\r\n(ঙ) সরকার কর্তৃক অর্পিত অন্য যে কোন দায়িত্ব সম্পাদন করা।\r\n'),
(2, 'বর্ডার গার্ড বাংলাদেশ-এর সদস্যগণের ক্ষমতা-ধারা ১২', '', 'সরকার কর্তৃক জারীকৃত প্রজ্ঞাপন দ্বারা নির্ধারিত শর্ত ও সীমা সাপেক্ষে, বর্ডার গার্ড বাংলাদেশ-এর এখতিয়ারভুক্ত এলাকায় উহার যে কোন সদস্য বা কোন নির্দিষ্ট শ্রেণির সদস্য অন্যান্য আইনসহ নিম্নের আইনসমূহ প্রয়োগ করতে পারেন, যা বন্যপ্রাণীর অবৈধ ব্যবসা দমনের জন্য প্রযোজ্য :\r\n(ঘ) বৈদেশিক মুদ্রা নিয়ন্ত্রণ আইন, ১৯৪৭ (১৯৪৭ সনের ৭ নং আইন)\r\n(ঙ) বাংলাদেশ প্রবেশ নিয়ন্ত্রণ আইন, ১৯৫২ (১৯৫২ সনের ৫৫ নং আইন)\r\n(চ) কাস্টমস আইন, ১৯৬৯ (১৯৬৯ সনের ৪ নং আইন) \r\nউপরোক্ত আইনের অধীন অপরাধ সংঘটনকারী ব্যক্তিকে গ্রেফতার, উক্ত অপরাধ সম্পর্কিত মালামাল আটক, উক্ত অপরাধ সংঘটিত হইয়াছে বা উহা সংঘটিত হইয়াছে মর্মে বিশ্বাস করিবার যুক্তিসঙ্গত কারণ রহিয়াছে এইরূপ কোন স্থানে বা কোন যানবাহনে প্রবেশ, তল্লাশি বা সংশ্লিষ্ট ব্যক্তির দেহ বা মালামাল তল্লাশির ক্ষেত্রে উক্ত আইনসমূহে উল্লিখিত কোনো কর্তৃপক্ষের বা পুলিশ বাহিনীর কোন নির্দিষ্ট স্তরের সদস্য কর্তৃক প্রয়োগযোগ্য কোনো নির্দিষ্ট বা সকল ক্ষমতা প্রয়োগ করিতে পারিবেন।\r\n\r\n'),
(3, 'গ্রেফতারকৃত ব্যক্তি, ইত্যাদি সোপর্দকরণ-ধারা ১৩', '', 'গণপ্রজাতন্ত্রী বাংলাদেশের সংবিধানের ৩৩(২) অনুচ্ছেদের বিধান সাপেক্ষে, বাহিনীর কোনো সদস্য কোনো ব্যক্তিকে গ্রেফতার বা কোনো মালামাল বা অন্য কোনো কিছু আটক করিলে, গ্রেফতারকৃত ব্যক্তিকে বা আটককৃত মালামাল বা অন্য কোন কিছু-\r\n\r\n(ক) সীমান্ত এলাকায় উক্ত গ্রেফতার বা আটকের ক্ষেত্রে, সংশ্লিষ্ট আইনে উল্লিখিত কর্তৃপক্ষ এবং উক্ত আইনে এইরূপ কোন কর্তৃপক্ষ নির্ধারিত না থাকিলে, নিকটবর্তী থানা কর্তৃপক্ষের হেফাজতে সোপর্দ করিবেন;\r\n(খ) বিধি অনুসারে বাহিনীর এখতিয়ারভুক্ত অন্য কোন এলাকায় উক্ত গ্রেফতার বা আটকের ক্ষেত্রে, সংশ্লিষ্ট আইনে উল্লিখিত কর্তৃপক্ষ এবং উক্ত আইনে এইরূপ কোন কর্তৃপক্ষ নির্ধারিত না থাকিলে, উক্ত গ্রেফতারের স্থান বা আটকস্থানের উপর এখতিয়ারসম্পন্ন থানা কর্তৃপক্ষের হেফাজতে সোপর্দ করিবেন।\r\n');
