INSERT INTO `customs_law` (`Id`, `Section`, `Sub-section`, `Details`) VALUES
(1, 'পণ্য আমদানি এবং রপ্তানি নিষিদ্ধ অথবা নিয়ন্ত্রণ করিবার ক্ষমতা-ধারা ১৬ ', '', 'সরকার, সময়ে সময়ে, সরকারি গেজেটে প্রজ্ঞাপন দ্বারা, কোনো নির্দিষ্ট বর্ণনার পণ্য বিমান, সমুদ্র অথবা স্থলপথে বাংলাদেশের অভ্যন্তরে আনা অথবা বাংলাদেশের বাহিরে নেওয়া নিষিদ্ধ অথবা নিয়ন্ত্রণ করিতে পারিবে।\r\n'),
(2, '\"চোরাচালান\"-এর সংজ্ঞা-ধারা ১৬ (২) (ধ)(ঈ)\r\n', '', 'আপাতত বলবৎ কোনো নিষিদ্ধকরণ অথবা নিয়ন্ত্রণ লংঘন করিয়া অথবা আরোপণীয় কাস্টমস্-শুল্ক ও কর ফাঁকি দিয়া ঘোষিত রুট ব্যতীত এবং কোনো কাস্টমস্ স্টেশন ব্যতীত অন্য কোনো স্থান হইতে অন্য কোনো পণ্য বাংলাদেশের অভ্যন্তরে আনয়ন করা অথবা বাহিরে নেওয়া; এবং উক্ত পণ্যসমূহ উল্লেখিতভাবে আনয়ন করিবার অথবা বাহিরে নেওয়ার জন্য কোনো প্রচেষ্টা, প্ররোচনা অথবা সমর্থন করাকেও বুঝায় ।\r\nবন্যপ্রাণীর অবৈধ ব্যবসা/চোরাচালান এই ধারায় প্রাসঙ্গিক হবে।\r\n'),
(3, 'গ্রেফতার করিবার ক্ষমতা-ধারা ১৬১', 'উপ-ধারা (১)', 'এতদুদ্দেশ্যে ক্ষমতাপ্রাপ্ত কোনো কাস্টমস্ কর্মকর্তার যদি বিশ্বাস করিবার কারণ থাকে যে কোনো ব্যক্তি এই আইনের অধীন কোন অপরাধ সংঘটিত করিয়াছেন, তাহা হইলে তিনি উক্ত ব্যক্তিকে গ্রেফতার করিতে পারিবেন।\r\n'),
(4, 'গ্রেফতার করিবার ক্ষমতা-ধারা ১৬১', 'উপ-ধারা (২)', 'চোরাচালান নিরোধ কাজে যথাযথভাবে ক্ষমতাপ্রাপ্ত কোনো ব্যক্তির যদি বিশ্বাস করিবার কারণ থাকে যে, কোনো ব্যক্তি এই আইনের অধীন কোনো চোরাচালানের অপরাধ সংঘটন করিয়াছেন, তাহা হইলে তিনি উক্ত ব্যক্তিকে গ্রেফতার করিতে পারিবেন।\r\n'),
(5, 'অপরাধসমূহের জন্য শাস্তি-ধারা ১৫৬', 'উপ-ধারা (১)', '(অনুচ্ছেদ ৮।) যদি কোনো পণ্য চোরাচালান করিয়া বাংলাদেশের অভ্যন্তরে আনা হয় অথবা বাংলাদেশের বাহিরে নেওয়া হয়, তাহা হইলে উক্ত পণ্য বাজেয়াপ্তযোগ্য হইবে এবং অপরাধটির সাথে সংশ্লিষ্ট কোনো ব্যক্তি পণ্য মূল্যের অনধিক দশগুণ পরিমাণ অর্থদণ্ডে দণ্ডনীয় হইবেন; এবং কোনো ম্যাজিস্ট্রেট কর্তৃক দোষী সাব্যস্ত হইলে তিনি অনধিক ছয় বৎসর মেয়াদের কারাদণ্ডে এবং তদুপরি উক্ত পণ্যের মূল্যের অনধিক দশগুণ পরিমাণ অর্থদণ্ডে দণ্ডনীয় হইবেন।\r\n'),
(6, 'কাস্টমস্ কর্মকর্তাগণের উপর প্রথম শ্রেণির ম্যাজিস্ট্রেটের কতিপয় ক্ষমতা অর্পণ-ধারা ১৫৮ক\r\n', '', 'এই আইনের অধীন প্রবেশ, তল্লাশি, আটক এবং গ্রেফতারের উদ্দেশ্য পূরণকল্পে সরকার, সরকারি গেজেটে প্রজ্ঞাপন দ্বারা, ফৌজদারী কার্যবিধি, ১৮৯৮ (১৮৯৮ এর ৫ নং আইন)-এর তৃতীয় তফসিলে বর্ণিত প্রথম শ্রেণির ম্যাজিস্ট্রেটের ক্ষমতা সহকারী কমিশনার পদমর্যাদার নিম্নে নহেন এইরূপ কোনো কর্মকর্তার উপর অর্পণ করিতে পারিবে।'),
(7, 'সংক্ষিপ্ত বিচার করিবার ক্ষমতা-ধারা ১৮৪', '', 'ফৌজদারী কার্যবিধি, ১৮৯৮ (১৮৯৮ সনের ৫নং আইন)-এর ধারা ২৬০-এর উপ-ধারা (১)-এ বর্ণিত অপরাধসমূহ সংক্ষিপ্ত পদ্ধতিতে বিচার করিবার জন্য আপাতত ক্ষমতাপ্রাপ্ত কোনো ম্যাজিস্ট্রেট বাদীর আবেদনক্রমে এই আইনের অধীন কোনো অপরাধে উহার সাথে সংশ্লিষ্ট পণ্যের মূল্য পাঁচ হাজার টাকার অধিক না হইলে, তিনি উপযুক্ত বিবেচনা করিলে উক্ত কার্যবিধির ধারা ২৬২-এর উপ-ধারা (১) এবং ধারা ২৬৩, ২৬৪ ও ২৬৫-এর বিধানাবলি অনুসারে সংক্ষিপ্ত বিচার করিতে পারিবেন।\r\n'),
(8, 'ম্যাজিস্ট্রেটগণের বিশেষ ক্ষমতা–ধারা ১৮৫', '', 'ফৌজদারী কার্যবিধি, ১৮৯৮ (১৮৯৮ সনের ৫ নং আইন) এ যাহা কিছুই থাকুক না কেন এবং এই আইনের অন্যান্য বিধানাবলি সাপেক্ষে, এতদূবিষয়ে সরকার কর্তৃক বিশেষভাবে ক্ষমতাপ্রাপ্ত কোনো প্রথম শ্রেণির ম্যাজিস্ট্রেট এই আইনের অধীন কোনো অপরাধের জন্য পাঁচ বৎসরের অধিক কারাদণ্ড এবং দশ হাজার টাকার অধিক অর্থ দণ্ড প্রদান করিতে পারিবেন।\r\n');
