INSERT INTO `forest_law` (`Id`, `Section`, `Sub-section`, `Details`) VALUES
(1, 'সংরক্ষিত বনে নিষিদ্ধ কার্যাদি-ধারা ২৬\r\n', '', 'উপ-ধারা ১ক এর দফা (চ) কোন ব্যক্তি সরকার কর্তৃক এতদ্বিষয়ে প্রণীত কোন বিধি লংঘন করে শিকার করেন, গুলি করেন, মাছ ধরেন, পানি বিষাক্ত করেন অথবা ফাঁদ বা ফাঁস পাতেন তার, বনের ক্ষতি সাধন করার জন্য দণ্ড প্রদানকারী আদালত তাকে যে ক্ষতিপূরণ প্রদানের নির্দেশ প্রদান করেন এরূপ ক্ষতিপূরণ প্রদানের অতিরিক্ত নিম্নে ছয় মাস পর্যন্ত ও উর্দ্ধে পাঁচ বছর পর্যন্ত কারাদণ্ড এবং নিম্নে পাঁচ হাজার টাকা পর্যন্ত ও উর্দ্ধে পঞ্চাশ হাজার টাকা পর্যন্ত জরিমানা হবে।\r\n'),
(2, 'বাজেয়াপ্তযোগ্য সম্পত্তি জব্দকরণ-ধারা ৫২', 'উপ-ধারা (১)', 'যে ক্ষেত্রে কোন বনজদ্রব্য সম্পর্কে কোন বন অপরাধ সংঘটিত হয়েছে বলে বিশ্বাস করার কারণ রয়েছে,সেক্ষেত্রে এরূপ অপরাধ সংঘটনে ব্যবহৃত সকল যন্ত্রপাতি,জলযান,যানবাহন বা গবাদি পশুসহ এরূপ দ্রব্য,কোন বন কর্মকর্তা বা পুলিশ কর্মকর্তা বা বর্তমানে বলবৎ অন্যকোন আইন দ্বারা বা আইনের অধীন এতদুদ্দেশ্যে ক্ষমতাপ্রাপ্ত যে কোন কর্মকর্তা জব্দ করতে পারবেন।'),
(3, 'বাজেয়াপ্তযোগ্য সম্পত্তি জব্দকরণ-ধারা ৫২', 'উপ-ধারা (১ক)', 'বন কর্মকর্তা ব্যতীত প্রত্যেক কর্মকর্তা এ ধারার অধীন যে সম্পত্তি জব্দ করেন তা উপ-ধারা (১) বর্ণিত সকল জব্দকৃত সম্পত্তি অপরাধীসহ নিকটবর্তী বন অফিসে পরবর্তী আইনগত কার্যক্রম গ্রহণের জন্য হস্তান্তর করবেন।\r\nতবে শর্ত থাকে যে, পুলিশ কর্মকর্তাগণকে অপরাধী হস্তান্তরের প্রয়োজন নাই, তবে এরূপ আটক সম্পর্কে এরূপ বন অফিসকে\r\nঅবহিত করবেন।\r\n'),
(4, 'বাজেয়াপ্তযোগ্য সম্পত্তি জব্দকরণ-ধারা ৫২', 'উপ-ধারা (২)', 'উপ-ধারা (২) অত্র ধারা অনুবলে কোন সম্পত্তি জব্দ করলে, জব্দকারী প্রত্যেক কর্মকর্তা, এরূপ সম্পত্তির উপর জব্দকৃত নির্দেশক চিহ্ন প্রদান করবেন, এবং যত শীঘ্র সম্ভব যে অপরাধের কারণে উহা জব্দ করা হয়েছে উক্ত অপরাধ বিচারের এখতিয়ারবান ম্যাজিস্ট্রেটের নিকট উক্ত জব্দকরণের বিষয়ে প্রতিবেদন প্রদান করবেন।\r\nতবে শর্ত থাকে যে, যে বনজ দ্রব্য সম্পর্কে অপরাধটি সংঘটিত হয়েছে বলে বিশ্বাস করা হয়েছে তা যদি সরকারি সম্পত্তি হয়, এবং অপরাধী যদি অজ্ঞাত হয়, সেক্ষেত্রে যত শীঘ্র সম্ভব, কর্মকর্তা, তার ঊর্ধ্বতন কর্মকর্তাকে পরিস্থিতি সম্পর্কিত একটি প্রতিবেদন পেশ করলে যথেষ্ট হবে।\r\n'),
(5, 'ধারা ৫২ অনুবলে জব্দকৃত সম্পত্তি অবমুক্ত করার ক্ষমতা-ধারা ৫৩\r\n\r\n', '', 'রেঞ্জার পদের নিম্নে নহেন এমন বন কর্মকর্তা, যে বা যার অধস্তন কেহ ৫২ ধারা অনুবলে কোন যন্ত্রপাতি, জলযান, যানবাহন অথবা গবাদি পশু জব্দ করে থাকলে, সে যে অপরাধের জন্য উহা জব্দ করা হয়েছে উক্ত অপরাধ বিচার করার এখতিয়ারবান ম্যাজিস্ট্রেটের নিকট, যদি এবং যখন প্রয়োজন হয়, অবমুক্তকৃত সম্পত্তি উপস্থিত করার শর্তে মালিক দ্বারা মুচলেকা সম্পাদন করার পর উহা অবমুক্ত করতে পারেন।\r\n'),
(6, 'উহার উপর কার্যক্রম-ধারা ৫৪', '', 'এরূপ কোন প্রতিবেদন প্রাপ্তির পর, ম্যাজিস্ট্রেট, হাজিরার সকল পদ্ধতি প্রয়োগে, আসামীকে গ্রেফতার ও বিচারের এবং আইনানুগভাবে সম্পত্তি ব্যবস্থাপনার জন্য প্রয়োজনীয় সকল পদক্ষেপ গ্রহণ করবেন।\r\n\r\n'),
(7, 'বনজদ্রব্য, যন্ত্রপাতি, ইত্যাদি কখন বাজেয়াপ্তযোগ্য-ধারা ৫৫', 'উপ-ধারা (১)', 'সকল কাঠ ও বনজদ্রব্য যা, সরকারি সম্পত্তি নহে এবং যার সম্পর্কে বন অপরাধ সংঘটিত হয়েছে এবং সকল যন্ত্রপাতি, নৌকা, যানবাহন এবং গবাদি পশু যা কোন বন অপরাধ সংঘটনের কাজে ব্যবহৃত হয়েছ তা বাজেয়াপ্তযোগ্য হবে।'),
(8, 'বনজদ্রব্য, যন্ত্রপাতি, ইত্যাদি কখন বাজেয়াপ্তযোগ্য-ধারা ৫৫', 'উপ-ধারা (২)', 'এরূপ বাজেয়াপ্তকরণ, এরূপ অপরাধের জন্য নির্ধারিত অন্য কোন দণ্ডের অতিরিক্ত হতে পারে।\r\n'),
(9, 'কতিপয় অপরাধ জামিন-অযোগ্য-ধারা ৬৩ক\r\n', '', 'আপততঃ বলবৎ অন্য কোন আইনে যাই থাকুক না কেন ২৬ ধারার (১ক) উপ-ধারা, ৩৩ ধারার (১ক) উপ-ধারা এবং ৬৩ ধারার শাস্তিযোগ্য বন অপরাধ জামিন অযোগ্য হবে।\r\n'),
(10, 'পরোয়ানা ব্যতীত প্রেপ্তারের ক্ষমতা-ধারা ৬৪', 'উপ-ধারা (১)', 'এক মাস বা ততোধিক কারাদণ্ড যোগ্য কোন অপরাধের সহিত কোন ব্যক্তি জড়িত আছেন বলে যুক্তি সঙ্গত সন্দেহ থাকলে যে কোন বন কর্মকর্তা বা পুলিশ কোন ম্যাজিস্ট্রেট এর আদেশ বা পরোয়ানা ব্যতিরেকে এরূপ ব্যক্তিকে গ্রেফতার করতে পারেন।'),
(11, 'পরোয়ানা ব্যতীত প্রেপ্তারের ক্ষমতা-ধারা ৬৪', 'উপ-ধারা (২)', 'অত্র ধারা অনুবলে গ্রেপ্তার করে প্রত্যেক কর্মকর্তা অপ্রয়োজনীয় বিলম্ব ব্যতিরেকে এবং অত্র আইনের বিধানমতে মুচলেকায় জামিনে মুক্তি প্রদান সাপেক্ষে,গ্রেফতারকৃত ব্যক্তিকে উক্ত মামলার এখতিয়ারবান ম্যাজিস্ট্রেট এর নিকট বা নিকটতম থানার ভারপ্রাপ্ত কর্মকর্তার নিকট নিয়ে যাবেন বা প্রেরণ করবেন।'),
(12, 'গ্রেফতারকৃত ব্যক্তিকে মুচলেকায় মুক্তি প্রদানের ক্ষমতা-ধারা ৬৫', '', 'রেঞ্জার পদের অধস্তন নহে এমন কোন বন কর্মকর্তা যিনি নিজে অথবা এর অধস্তন কোন কর্মকর্তা যখন ৬৪ ধারা অনুবলে কোন ব্যক্তিকে গ্রেফতার করেন; তিনি এরূপ ব্যক্তি কর্তৃক মামলাটি বিচারের জন্য এখতিয়ারাধীন ম্যাজিস্ট্রেটের নিকট অথবা নিকটতম থানার ভারপ্রাপ্ত কর্মকর্তার নিকট যদি এবং যখন প্রয়োজন হয় হাজির হবার মুচলেকা সম্পাদনের প্রেক্ষিতে, এরূপ ব্যক্তিকে মুক্তি প্রদান করতে পারেন। (তবে শর্ত থাকে যে, যেক্ষেত্রে গ্রেফতারকৃত ব্যক্তি এ আইনের আওতায় জামিন অযোগ্য কোন অপরাধ করেছেন বলে সন্দেহ হয়, সেক্ষেত্রে এ ধারার কোন কিছুই প্রযোজ্য হবে না।)'),
(13, 'অপরাধ সংঘটনে বাধা প্রদানের ক্ষমতা-ধারা ৬৬', '', 'প্রত্যেক বন কর্মকর্তা এবং পুলিশ কর্মকর্তা, যে কোন বন অপরাধ সংঘটনে বাধা প্রদান করবেন এবং বাধা প্রদানের উদ্দেশ্যে হস্তক্ষেপ করতে পারেন।'),
(14, 'অপরাধ সংক্ষিপ্ত ভাবে বিচারের ক্ষমতা-ধারা ৬৭\r\n', '', 'জেলা ম্যাজিস্ট্রেট বা সরকার কর্তৃক এতদ্বিষয়ে বিশেষভাবে ক্ষমতাপ্রাপ্ত কোন প্রথম শ্রেণীর ম্যাজিস্ট্রেট অনধিক দুই বছর মেয়াদ পর্যন্ত দণ্ডনীয় অথবা অনধিক দশ হাজার টাকা পর্যন্ত জরিমানার অথবা উভয়বিধ দণ্ডনীয় কোন অপরাধের বিচার ১৮৯৮ সনের ফৌজদারী কার্যবিধির আওতায় সংক্ষিপ্ত ভাবে সম্পাদন করতে পারবেন।\r\n'),
(15, 'ফরেস্ট ম্যাজিস্ট্রেট নিয়োগ-ধারা ৬৭ক\r\n', 'উপ-ধারা (১)', 'সরকার, সরকারি গেজেটে প্রজ্ঞাপনের মাধ্যমে এক বা একাধিক প্রথম শ্রেণির ম্যাজিস্ট্রেটকে এককভাবে এই আইনের আওতায় অপরাধ বিচার করার জন্য নিয়োগ দিতে পারেন। এবং এরূপ ম্যাজিস্ট্রেটের ভৌগোলিক এখতিয়ারও সুনির্দিষ্ট করে দিতে পারেন।'),
(16, 'ফরেস্ট ম্যাজিস্ট্রেট নিয়োগ-ধারা ৬৭ক', 'উপ-ধারা (২)', 'বর্তমানে বলবৎ অন্য কোন আইনে যাই বলা হোক না কেন এরূপ ফরেষ্ট ম্যাজিষ্ট্রেটগনের এই আইনে নির্ধারিত শাস্তি প্রদানের ক্ষমতা থাকবে।'),
(17, 'বন অপরাধের মামলা পরিচালনা-ধারা ৬৯ক\r\n', '', 'আপতত বলবৎ অন্য কোন আইনে যা কিছুই থাকুক না কেন, সরকার ডেপুটি রেঞ্জার এর অধস্তন নহে এমন যে কোন বন কর্মকর্তাকে যে কোন আদালতে বিচারাধীন যে কোন বন মামলা সংক্রান্ত বিষয়ে সরকারের পক্ষে উপস্থিত হবার, ওকালতি করার এবং মামলা পরিচালনা করার ক্ষমতা প্রদান করতে পারেন।\r\n'),
(18, 'যে সকল ব্যক্তি বন কর্মকর্তা ও পুলিশ কর্মকর্তাকে সহায়তা করতে বাধ্য-ধারা ৭৯', 'উপ-ধারা (১)', ' সংরক্ষিত বন ও রক্ষিত বনে অধিকার প্রয়োগকারী প্রত্যেক ব্যক্তি, বা যিনি এরূপ বনের বনজ দ্রব্য নিয়ে যাবার জন্য, বা কাঠ কর্তনের এবং অপসারণের জন্য অথবা গবাদি পশু চারণের জন্য অনুমতি প্রাপ্ত এবং প্রত্যেক ব্যক্তি, যিনি এরূপ ব্যক্তিবর্গ কর্তৃক নিয়োজিত, এবং সরকার বা স্থানীয় কর্তৃপক্ষ কর্তৃক এরূপ বন সংলগ্ন গ্রামে নিয়োজিত প্রত্যেক ব্যক্তি, অথবা, সেবা প্রদানের উদ্দেশ্যে সরকার বা স্থানীয় কর্তৃপক্ষ থেকে বেতন-ভাতাদি প্রাপ্ত কোন সম্প্রদায়ের প্রত্যেক ব্যক্তি, কেহ কোন বন অপরাধ সংঘটন কর্মকর্তাকে উক্ত সংবাদ প্রদান করতে বাধ্য থাকবেন, এবং কোন বন কর্মকর্তা বা পুলিশ কর্মকর্তার প্রয়োজন হোক বা না হোক তিনি অবিলম্বে পদক্ষেপ গ্রহণ করবেন -\r\n(ক) এরূপ বনে যে অগ্নি সম্পর্কে তিনি সংবাদ পেয়েছেন বা জ্ঞাত হয়েছেন উহা নির্বাপণ করতে,\r\n(খ) এরূপ বনের নিকটবর্তী কোন স্থানে যে অগ্নি সম্পর্কে তিনি সংবাদ পেয়েছেন বা জ্ঞাত হয়েছেন, ইহার বিস্তার রোধকল্পে তিনি তার ক্ষমতার মধ্যে আইনগত পন্থায় বাধা প্রদান করতে, এবং কোন বন কর্মকর্তা বা পুলিশ কর্মকর্তা তার সাহায্য চাইলে তিনি পদক্ষেপ গ্রহণ করবেন,\r\n(গ) এরূপ বনে কোন বন অপরাধ সংঘটনে ব'),
(19, 'যে সকল ব্যক্তি বন কর্মকর্তা ও পুলিশ কর্মকর্তাকে সহায়তা করতে বাধ্য-ধারা ৭৯', 'উপ-ধারা (২)', '(ক) (১) উপ-ধারা মতে অপ্রয়োজনীয় বিলম্ব না করে নিকটতম বন কর্মকর্তা বা পুলিশ কর্মকর্তাকে সংবাদ প্রদান করতে;\r\n(খ) (১) উপ-ধারা মতে সংরক্ষিত বা রক্ষিত বনে কোন অগ্নি নির্বপণ করার পদক্ষেপ গ্রহণ করতে;\r\n(গ) (১) উপ-ধারা মতে এরূপ কোন বনের নিকটবর্তী কোন স্থানের অগ্নি এরূপ বনে বিস্তার লাভ করতে বাধা প্রদান করতে;\r\nঅথবা\r\n(ঘ) কোন বন বা পুলিশ কর্মকর্তা, এরূপ বনে কোন বন অপরাধ সংঘটনে বাধা প্রদান করতে, অথবা এরূপ বনে কোন বন অপরাধ সংঘটিত হয়েছে বিশ্বাস করার যুক্তিসংগত কারণ থাকলে, অপরাধীকে খুঁজে বের করতে এবং গ্রেফতার করতে সাহায্য চাইলে, সাহায্য করতে;\r\n\r\nতিনি ছয় মাস পর্যন্ত কারাদণ্ডে বা দুই হাজার টাকা পর্যন্ত জরিমানায় অথবা উভয় দণ্ডে দণ্ডিত হবেন।');
INSERT INTO `import_and_export_law` (`Id`, `Section`, `Sub-section`, `Details`) VALUES
(1, 'আমদানি এবং রপ্তানি নিষিদ্ধ বা সীমিত করিবার ক্ষমতা-ধারা ৩', 'উপ-ধারা (১) ', 'সরকার, সরকারি গেজেটে প্রকাশিত আদেশের মাধ্যমে এবং আদেশের অধীনে এবং এই আদেশে বর্ণিত শর্ত এবং ব্যতিক্রম সাপেক্ষে নির্দিষ্ট বর্ণনার পণ্যের আমদানি বা রপ্তানি নিষিদ্ধ, সীমিত বা অন্যরূপে নিয়ন্ত্রণ করিতে পারিবে, অথবা সাধারণত এইরূপ পণ্য আমদানি বা রপ্তানির সাথে সংশ্লিষ্ট সকল কার্যক্রম (বাণিজ্য কার্যক্রমসহ) এবং পদ্ধতি নিয়ন্ত্রণ করিতে পারিবে, এবং এই আইনের আওতায় লাইসেন্সের জন্য আবেদন করিবার বিধান করিতে পারিবে, আবেদনের সাথে প্রমাণ যুক্ত করিবার, এবং এইরূপ লাইসেন্স মঞ্জুরী, ব্যবহার, স্থানান্তর, বিক্রয় বা বাতিল এবং যেই প্রকারে বা পদ্ধতিতে যে সময়ে আপিল এবং আবেদন পুনর্নিরীক্ষণ বা পুনর্বিবেচনা করা হইবে এবং নিষ্পত্তি করা হইবে, এবং ফি আদায়ের বিষয়ে বিধান করিতে পারিবে।\r\n'),
(2, 'আমদানি এবং রপ্তানি নিষিদ্ধ বা সীমিত করিবার ক্ষমতা-ধারা ৩', 'উপ-ধারা (২)', 'প্রধান নিয়ন্ত্রক বা সরকার কর্তৃক ক্ষমতাপ্রাপ্ত অন্য কোনো কর্মকর্তা কর্তৃক প্রদত্ত লাইসেন্সের শর্তাবলি ব্যতীত নির্ধারিত বর্ণনার কোনো পণ্য আমদানি বা রপ্তানি করা যাইবে না।'),
(3, 'আমদানি এবং রপ্তানি নিষিদ্ধ বা সীমিত করিবার ক্ষমতা-ধারা ৩\r\n', 'উপ-ধারা (৩)', 'সকল পণ্য যাহার উপর উপ-ধারা (১)-এর অধীনে যে কোনও আদেশ প্রযোজ্য উহা কাস্টমস আইন, ১৯৬৯ (১৯৬৯ সনের ৪ নং আইন) এর ধারা ১৬-এর অধীনে আমদানি বা রপ্তানি নিষিদ্ধ বা সীমিত করা হইয়াছে বলিয়া গণ্য হইবে এবং তদনুযায়ী এই আইনের সকল বিধানাবলি কার্যকর হইবে।');
