INSERT INTO `penal_law` (`Id`, `Section`, `Sub-section`, `Details`) VALUES
(1, 'দশ টাকা মূল্যের কোনো প্রাণী বধ বা বিকলাঙ্গ করিয়া অনিষ্ট সাধন-ধারা ৪২৮', '', 'যে ব্যক্তি দশ টাকা বা তদূর্ধ্ব মূল্যের কোনো প্রাণী বধ, বিষ প্রয়োগ, বিকলাঙ্গ করে বা ব্যবহারের অযোগ্যে পরিণত করিয়া অনিষ্ট সাধন করে, সেই ব্যক্তি যে কোনো বর্ণনার সর্বোচ্চ দুই বৎসর কারাদণ্ড বা অর্থদণ্ড বা উভয়বিধ দণ্ডে দণ্ডিত হইবে।'),
(2, 'যে কোনো মূল্যের গবাদি পশু ইত্যাদি বা পঞ্চাশ টাকা মূল্যের কোনো প্রাণী হত্যা বা বিকলাঙ্গ করিয়া অনিষ্ট সাধন–ধারা ৪২৯', '', 'যে ব্যক্তি, যে কোনো মূল্যের কোনো হাতি, উট, ঘোড়া, খচ্চর, মহিষ, ষাঁড়, গাভী বা ছিন্নমুগ্ধ ষাঁড় অথবা পঞ্চাশ টাকা বা তদূর্ধ্ব মূল্যের যে কোনো প্রাণীকে হত্যা করে, বিষ প্রয়োগ করে, বিকলাঙ্গ করে বা ব্যবহারের অযোগ্য পরিণত করিয়া অনিষ্ট সাধন করে, সেই ব্যক্তি যে কোনো বর্ণনার সর্বোচ্চ পাঁচ বৎসর কারাদণ্ড বা অর্থদণ্ড বা উভয়বিধ দণ্ডে দণ্ডিত হইবে।\r\n');
INSERT INTO `police_law` (`Id`, `Section`, `Sub-section`, `Details`) VALUES
(1, 'রাস্তা ইত্যাদিতে কতিপয় সংঘটিত অপরাধের শান্তি ইত্যাদি – পুলিশ কর্মকর্তাদের ক্ষমতা–ধারা ৩৪', '', 'কোনো ব্যক্তি যিনি - কোনো উন্মুক্ত স্থানে বা সড়ক বা শহরের সীমানার মধ্যে জনসাধারণের চলাচলের রাস্তায় যেখানে সরকার কর্তৃক এই ধারা বিস্তৃত হইবে- নিম্নের যে কোনো একটি অপরাধ সংঘটন করেন, বাসিন্দা বা যাত্রীদের বাধা, অসুবিধা, বিরক্ত, ঝুঁকি, বিপদ বা ক্ষতি, ম্যাজিস্ট্রেটের নিকট দোষী সাব্যস্ত হইলে, তিনি অনূর্ধ্ব পঞ্চাশ টাকা অর্থদণ্ড, বা কঠোর কায়িক শ্রমসহ বা ব্যতীত অনূর্ধ্ব আট দিনের কারাদণ্ডে দণ্ডনীয় হইবেন; এবং কোনো পুলিশ অফিসারের দৃষ্টিতে কেহ নিম্নবর্ণিত কোনো অপরাধ সংঘটন করিলে তবে তাহার এইরূপ অপরাধ সংঘটনকারীকে বিনা পরোয়ানায় হেফাজতে নেয়া বৈধ হইবে, যথা :\r\nপ্রাণীর প্রতি নিষ্ঠুরতা –\r\nদ্বিতীয় – অযৌক্তিকভাবে বা নির্মমভাবে যে কোনো ব্যক্তি কোনো প্রাণীকে প্রহার করে, অপব্যবহার করে বা নির্যাতন করে।\r\n'),
(2, 'পুলিশ-অফিসার তথ্য, ইত্যাদি সরবরাহ করিতে পারেন–ধারা ২৪', '', 'কোনো পুলিশ-অফিসারের পক্ষে ম্যাজিস্ট্রেটের নিকট কোনো তথ্য প্রদান এবং অপরাধ সংঘটনকারী কোনো ব্যক্তির বিরুদ্ধে আইন অনুযায়ী সমন, পরোয়ানা বা তল্লাশি-পরোয়ানা বা এইরূপ কোনো আইনি প্রক্রিয়ার জন্য আবেদন করা বৈধ হইবে।\r\n'),
(3, 'পুলিশ-অফিসার দাবিহীন সম্পত্তির দায়িত্ব নিবেন; এবং যাহা ম্যাজিস্ট্রেটের আদেশ সাপেক্ষে বিলিবন্দেজযোগ্য–ধারা ২৫\r\n', '', 'প্রত্যেক পুলিশ-অফিসারের দায়িত্ব হইবে দাবিহীন সম্পত্তির দায়িত্ব গ্রহণ করা এবং উহার একটি তালিকা জেলা ম্যাজিস্ট্রেটের নিকট প্রেরণ করা, পুলিশ-অফিসার জেলা ম্যাজিস্ট্রেটের নিকট হইতে এইরূপ আদেশের মাধ্যমে এইরূপ সম্পত্তি বিলিবন্দেজের জন্য নির্দেশনা প্রাপ্ত হইবে।\r\n\r\n');
INSERT INTO `suspects_law` (`Id`, `Section`, `Sub-section`, `Details`) VALUES
(1, 'কে সাক্ষ্য দিতে পারে-ধারা ১১৮', '', 'সকল ব্যক্তিই সাক্ষ্য দিতে সক্ষম, যতক্ষণ না আদালত মনে করেন যে, অপরিণত বয়স, অতি বার্ধক্য, দৈহিক বা মানসিক ব্যাধি, অথবা এরূপ অন্য কোনো কারণে, সে কোনো প্রশ্ন বুঝতে বা প্রশ্নের যুক্তিসংগত জবাব দিতে সে ব্যর্থ।\r\nব্যাখ্যা – কোনো মানসিক প্রতিবন্ধী বিচারের অনুপযুক্ত নয়, যতক্ষণ না তার মানসিক প্রতিবন্ধিতা প্রমাণ করে যে, সে কোনো প্রশ্ন বুঝতে এবং ঐ প্রশ্নের যুক্তিসংগত জবাব দিতে পারবে না।\r\n'),
(2, 'বাকশক্তিহীন বা মূক সাক্ষী-ধারা ১১৯', '', 'এমন কি একজন সাক্ষী যদি কথা বলতে অসমর্থ হয়, তার সাক্ষ্য উপস্থাপনের জন্য এমন কৌশল অবলম্বন করা যায় যার দ্বারা সে তাকে বুঝাতে পারে, যেমন লিখিত বক্তব্য বা ইশারা প্রদানের মাধ্যমে; তবে এ লেখা বা ইশারা অবশ্যই আদালতের সামনে লিখতে ও করতে হতে হবে। এভাবে প্রদত্ত সাক্ষ্য মৌখিক সাক্ষ্য হিসাবে বিবেচিত হবে।\r\n'),
(3, 'বেঙ্গল পুলিশ প্রবিধান (পিআরবি), ১৯৪৩\r\n\r\nপ্রবিধান–২৬৭\r\n\r\n', '', 'একজন পুলিশ কর্মকর্তা আমলযোগ্য অপরাধে অভিযুক্ত ব্যক্তি মানসিক প্রতিবন্ধী কিনা সে সিদ্ধান্ত নিবেন। তিনি মামলাটি তদারকি করবেন যদি মনে করেন যে, কোন ব্যক্তি মানসিক রোগে আক্রান্ত, এবং অপরাধ সংঘটন করেছেন, তিনি আসামীকে বিচারের জন্য আদালতে প্রেরণ করবেন। তবে তদন্তকারী কর্মকর্তা আদালতকে উক্ত আসামীর মধ্যে কোনো মানসিক অসুস্থতার লক্ষণ পরিলক্ষিত হওয়া মাত্র তার মানসিক স্বাস্থ্য সম্পর্কে তদন্তের জন্য অনুরোধ জানাবেন এবং আদালতের বিবেচনায় বন্দী আসামী তার পক্ষ সমর্থনের জন্য সামর্থ কিনা এ সম্পর্কে অগ্রিম নিশ্চিত না হয়ে আদালতে সাক্ষ্য প্রদানের জন্য কোনো সাক্ষী প্রেরণ করবেন না।\r\n'),
(4, 'ফৌজদারী কার্যবিধি, ১৮৯৮ (১৮৯৮ সনের ৫ নং আইন)*\r\n\r\nজামিনযোগ্য, আমলযোগ্য এবং অ-আমলযোগ্য অপরাধের সংজ্ঞা-ধারা ৪(১)\r\n', '', '(খ)\"জামিনযোগ্য অপরাধ\" হচ্ছে সেই সকল অপরাধ যা ফৌজদারী কার্যিবিধির দ্বিতীয় তফসিলে জামিনযোগ্য হিসাবে দেখানো হয়েছে, বা যা বর্তমানে বলবৎ কোনো আইন দ্বারা জামিনযোগ্য করা হয়েছে; এবং এছাড়া অন্যান্য সকল অপরাধ হচ্ছে “অ-জামিনযোগ্য অপরাধ”।\r\n(চ) “আমলযোগ্য অপরাধ\" এবং \"আমলযোগ্য মামলা\" হচ্ছে এমন সকল অপরাধ এবং মামলার জন্য, পুলিশ কর্মকর্তা ফৌজদারী কার্যবিধির দ্বিতীয় তফসিল অনুসারে বা বর্তমানে বলবৎ কোনো আইনের আওতায়, বিনা পরোয়ানায় গ্রেফতারকরতে পারেন।  \r\n(ঢ) “অ-আমলযোগ্য অপরাধ\" এবং \"অ-আমলযোগ্য মামলা” হচ্ছে এমন সকল অপরাধ ও মামলা, যার জন্য কোনো পুলিশ কর্মকর্তা বিনা পরোয়ানায় গ্রেফতার করতে পারেন না।\r\n'),
(5, 'মানসিক অসুস্থ আসামীর ক্ষেত্রে মামলার রীতি-ধারা ৪৬৪\r\n', 'উপ-ধারা (১)', 'অনুসন্ধানকারী বা বিচারকারী ম্যাজিস্ট্রেট-এর যদি বিশ্বাস করার মত যুক্তিসঙ্গত কারণ থাকে যে, আসামী মানসিক অসুস্থ এবং ফলে আত্মপক্ষ সমর্থনে অপারগ, তাহলে ম্যাজিস্ট্রেট মানসিক অসুস্থতা সম্পর্কে তদন্ত করবেন এবং তাকে জেলার সিভিল সার্জন বা সরকার নির্দেশিত অপর কোনো মেডিক্যাল অফিসার দ্বারা পরীক্ষা করাবেন, এবং অতঃপর সাক্ষী হিসাবে উক্ত সার্জন বা অফিসারের জবানবন্দী গ্রহণ করবেন এবং এ জবানবন্দী লিখে নিবেন। \r\n'),
(6, 'মানসিক অসুস্থ আসামীর ক্ষেত্রে মামলার রীতি-ধারা ৪৬৪\r\n', 'উপ-ধারা (১ক)', 'এরূপ পরীক্ষা এবং তদন্ত সাপেক্ষে ম্যাজিস্ট্রেট ফৌজদারী কার্যবিধির ৪৬৬ ধারার বিধান অনুযায়ী অভিযুক্তদের বিষয়ে অগ্রসর হতে পারবেন।\r\n'),
(7, 'মানসিক অসুস্থ আসামীর ক্ষেত্রে মামলার রীতি-ধারা ৪৬৪', 'উপ-ধারা (২)', 'ম্যাজিস্ট্রেট যদি মনে করেন যে, আসামী মানসিক অসুস্থ এবং ফলে আত্মপক্ষ সমর্থনে অপারগ, তাহলে তিনি তদমর্মে অভিমত লিপিবদ্ধ করবেন এবং মামলার কার্যধারা স্থগিত রাখবেন।'),
(8, 'মানসিক অসুস্থ ব্যক্তির ক্ষেত্রে দায়রা আদালতে পদ্ধতি-ধারা ৪৬৫\r\n', 'উপ-ধারা (১)', 'দায়রা আদালতে বিচারের সময়ে কোনো ব্যক্তি মানসিক অসুস্থতার কারণে আত্মপক্ষ সমর্থনে অপারগ বলে আদালতের নিকট প্রতীয়মান হলে আদালত প্রথমে উক্ত মানসিক অসুস্থতা ও অসামর্থের বিষয়ে বিচার করবেন, এবং আদালত যদি সে বিষয়ে নিশ্চিত হন, তাহলে তদমর্মে একটি অভিমত লিপিবদ্ধ করবেন এবং মামলার পরবর্তী কার্যক্রম স্থগিত রাখবেন।\r\n'),
(9, 'মানসিক অসুস্থ ব্যক্তির ক্ষেত্রে দায়রা আদালতে পদ্ধতি-ধারা ৪৬৫', 'উপ-ধারা (২) ', 'আসামির মানসিক অসুস্থতা ও আত্মপক্ষ সমর্থনে অপরাগতার বিষয়ে বিচার আদালতে তার বিচারের অংশ বলে পরিগণিত হবে।\r\n'),
(10, 'তদন্ত বা বিচার স্থগিত সাপেক্ষে মানসিক অসুস্থ ব্যক্তির মুক্তি-ধারা ৪৬৬', 'উপ-ধারা (১)', 'যখন কোনো আসামী মানসিক অসুস্থ ও আত্মপক্ষ সমর্থনে অপরাগ বলে সাব্যস্ত হয়, তখন সংশ্লিষ্ট মামলা জামিন গ্রহণের উপযোগী হোক বা না হোক, ম্যাজিস্ট্রেট বা ক্ষেত্রমত আদালত, এ মর্মে পর্যাপ্ত জামানতে মুক্তি দিতে পারবেন যে, তার যথাযথ যত্ন নেয়া হবে এবং নিজের বা অপর কারো ক্ষতিসাধনে তাকে বাধা দেয়া হবে, এবং আবশ্যক হলে তাকে ম্যাজিস্ট্রেট বা আদালতের কাছে অথবা ম্যাজিস্ট্রেট বা আদালতের নির্দেশ অনুযায়ী এ বিষয়ে নিযুক্ত কোনো কর্মকর্তার কাছে উপস্থিত করা হবে।'),
(11, 'তদন্ত বা বিচার স্থগিত সাপেক্ষে মানসিক অসুস্থ ব্যক্তির মুক্তি-ধারা ৪৬৬', 'উপ-ধারা (২) ', 'ম্যাজিস্ট্রেট বা আদালত যদি মনে করেন যে, মামলাটি এরূপ যে জামিন গ্রহণ করা উচিৎ নয় অথবা যদি পর্যাপ্ত জামানত না দেয়া হয়, তাহলে ম্যাজিস্ট্রেট বা আদালত যেরূপ উপযুক্ত মনে করেন সেরূপ স্থান এবং পদ্ধতিতে আসামীকে নিরাপদ হেফাজতে আটক রাখার আদেশ দিবেন এবং গৃহীত ব্যবস্থা সম্পর্কে সরকারকে রিপোর্ট দিবেনঃ\r\nমানসিক অসুস্থ ব্যক্তির হেফাজত\r\nতবে শর্ত হচ্ছে, ১৯১২ সনের উন্মাদ আইন” অনুসারে সরকার কর্তৃক প্রণীত বিধি মোতাবেক ব্যতীত অপর কোনো উপায়ে অভিযুক্ত ব্যক্তিকে মানসিক হাসপাতালে ভর্তি বা আবদ্ধ করে রাখা যাবে না।\r\n'),
(12, 'গ্রেফতারের পদ্ধতি-ধারা ৪৬\r\n', 'উপ-ধারা (১) ', 'কথা অথবা কাজের দ্বারা হেফাজতে আত্মসমর্পণ না করলে পুলিশ কর্মকর্তা অথবা গ্রেফতারকারী অন্য কোনো ব্যক্তি গ্রেফতার করার সময় যাকে গ্রেফতার করা হচ্ছে প্রকৃতপক্ষেই তার দেহ স্পর্শ করবেন বা আটক করবেন।'),
(13, 'গ্রেফতারকৃত ব্যক্তিকে ২৪ ঘন্টার বেশি সময় আটক রাখা যাবে না-ধারা ৬১\r\n', '', 'কোনো পুলিশ কর্মকর্তা বিনা পরোয়ানায় গ্রেফতারকৃত কোনো ব্যক্তিকে সামগ্রিক পরিস্থিতি বিবেচনায় যুক্তিসংগত সময় অপেক্ষা অধিককাল আটক রাখবেন না এবং ১৬৭ ধারা অনুসারে কোনো ম্যাজিস্ট্রেটের বিশেষ আদেশ না থাকলে এরূপ সময় গ্রেফতারের স্থান হতে ম্যাজিস্ট্রেটের আদালতে যাবার সময় বাদ দিয়ে চব্বিশ ঘন্টার অধিক হবে না।\r\n'),
(14, 'শিশু আইন, ২০১৩ (২০১৩ সনের ২৪ নং আইন)\r\n\r\nশিশু-ধারা ৪\r\n', '', 'বিদ্যমান অন্য কোন আইনে ভিন্নতর যাহা কিছুই থাকুক না কেন, এই আইনের উদ্দেশ্য পূরণকল্পে অনূর্ধ্ব ১৮ (আঠার) বৎসর বয়স পর্যন্ত সকল ব্যক্তি শিশু হিসাবে গণ্য হবে।\r\n'),
(15, 'গ্রেফতার, ইত্যাদি-ধারা ৪৪\r\n', 'উপ-ধারা (১)', 'এই ধারায় যাহা কিছুই থাকুক না কেন, ৯ (নয়) বৎসরের নিম্নের কোন শিশুকে কোনো অবস্থাতেই গ্রেফতার করা বা, ক্ষেত্রমত, আটক রাখা যাইবে না। '),
(16, 'গ্রেফতার, ইত্যাদি-ধারা ৪৪\r\n', 'উপ-ধারা (৩) ', 'শিশুকে গ্রেফতার করিবার পর গ্রেফতারকারী পুলিশ কর্মকর্তা গ্রেফতারের কারণ, স্থান, অভিযোগের বিষয়বস্তু, ইত্যাদি সম্পর্কে তাৎক্ষণিক ভাবে শিশু বিষয়ক পুলিশ কর্মকর্তাকে অবহিত করিবেন এবং প্রাথমিকভাবে তাহার বয়স নির্ধারণ করিয়া নথিতে\r\nলিপিবদ্ধ করিবেন :\r\nতবে শর্ত থাকে যে, গ্রেফতার করার পর কোন শিশুকে হাতকড়া বা কোমরে দড়ি বা রশি লাগানো যাইবে না।\r\n'),
(17, 'তদন্ত-ধারা ৪৬', '', 'এই আইন বা ইহার অধীন প্রণীত বিধিতে সুস্পষ্ট ও ভিন্নরূপ কোন বিধান না থাকিলে, এই আইনের অধীন সকল তদন্ত কার্যক্রম পরিচালনার ক্ষেত্রে, ফৌজদারী কার্যবিধির বিধানাবলী, যতদূর সম্ভব, প্রযোজ্য ও অনুসরণ করিতে হবে।'),
(18, 'জবানবন্দী, সতর্কীকরণ ও মুক্তি-ধারা ৪৭\r\n\r\n ', 'উপধারা (১)', 'শিশুর মাতা-পিতা এবং তাহাদের উভয়ের অবর্তমানে তত্ত্বাবধানকারী অভিভাবক বা কর্তৃপক্ষ অথবা আইনানুগ বা বৈধ অভিভাবক বা ক্ষেত্রমত, বর্ধিত পরিবারের সদস্য এবং প্রবেশন কর্মকর্তা বা সমাজকর্মীর উপস্থিতিতে শিশুবিষয়ক পুলিশ কর্মকর্তা শিশুর জবানবন্দী গ্রহণ করিবেন।\r\n'),
(19, 'জবানবন্দী, সতর্কীকরণ ও মুক্তি-ধারা ৪৭\r\n', 'উপধারা (২) ', 'শিশুর বিরুদ্ধে আনীত অভিযোগের প্রকৃতি ও শিশুর মানসিক ও আর্থ-সামাজিক অবস্থা বিবেচনায় লইয়া শিশুবিষয়ক পুলিশ কর্মকর্তা –\r\n(ক) সংশ্লিষ্ট শিশু, শিশুর মাতা-পিতা এবং তাহাদের উভয়ের অবর্তমানে তত্ত্বাবধানকারী অভিভাবক বা কর্তৃপক্ষ অথবা আইনানুগ বা বৈধ অভিভাবক বা, ক্ষেত্রমত, বর্ধিত পরিবারের সদস্য এবং প্রবেশন কর্মকর্তা বা সমাজকর্মীর উপস্থিতিতে শিশুকে লিখিত বা মৌখিক সতর্কীকরণের পর মুক্তি প্রদান করিতে পারিবেন, যাহা শিশুর বিরুদ্ধে রেকর্ড হিসাবে গণ্য হইবে না; বা\r\n(খ) বিকল্প পন্থায় প্রেরণ করিতে পারিবেন।\r\n'),
(20, 'মানসিক স্বাস্থ্য আইন, ২০১৮ (২০১৮ সনের ৬০ নং আইন)\r\n\r\nমানসিক অবস্থার বিচারিক অনুসন্ধান-ধারা ২০\r\n', 'উপ-ধারা (১)', 'মানসিক অসুস্থতায় আক্রান্ত ব্যক্তির কোনো অভিভাবক বা আত্মীয় উক্ত ব্যক্তির মানসিক অবস্থা নিরূপণের জন্য আদালতে আবেদন করিতে পারিবে।'),
(21, 'মানসিক স্বাস্থ্য আইন, ২০১৮ (২০১৮ সনের ৬০ নং আইন)\r\n\r\nমানসিক অবস্থার বিচারিক অনুসন্ধান-ধারা ২০', 'উপ-ধারা (২) ', 'উপ-ধারা (১) এর অধীন আবেদন প্রাপ্তির পর আদালত কোনো দায়িত্বপ্রাপ্ত মেডিক্যাল অফিসারকে সময়সীমা নির্ধারণ পূর্বক মানসিক অসুস্থতায় আক্রান্ত হিসাবে অভিযুক্ত ব্যক্তির মানসিক অক্ষমতা চিহ্নিত ও যাচাই করিয়া প্রতিবেদন দাখিলের জন্য আদেশ প্রদান করিতে পারিবে।\r\n'),
(22, 'মানসিক স্বাস্থ্য আইন, ২০১৮ (২০১৮ সনের ৬০ নং আইন)\r\n\r\nমানসিক অবস্থার বিচারিক অনুসন্ধান-ধারা ২০', 'উপ-ধারা (৩) ', 'উপ-ধারা (২) এর অধীন আদেশ প্রাপ্তির পর দায়িত্বপ্রাপ্ত মেডিক্যাল অফিসার অভিযুক্ত ব্যক্তিকে নির্ধারিত সময় ও স্থানে হাজির করিবার জন্য আবেদনকারীসহ সংশ্লিষ্টকে নোটিশ প্রদান করিবে :\r\nতবে শর্ত থাকে যে, অভিযুক্ত ব্যক্তি মহিলা হইলে এবং ধর্ম বা প্রথানুযায়ী জনসম্মুখে উপস্থিত হইবার বাধা থাকিলে, আদালত সুবিধাজনক স্থানে তাহাকে পরীক্ষার ব্যবস্থা করিবে।\r\n\r\n'),
(23, 'মানসিক স্বাস্থ্য আইন, ২০১৮ (২০১৮ সনের ৬০ নং আইন)\r\n\r\nমানসিক অবস্থার বিচারিক অনুসন্ধান-ধারা ২০', 'উপ-ধারা (৪) ', 'অনুসন্ধান সমাপ্তির পর দায়িত্বপ্রাপ্ত মেডিক্যাল অফিসার প্রতিবেদন দাখিল করিলে আদালত অভিযুক্ত ব্যক্তির মানসিক সুস্থতা এবং সম্পত্তি রক্ষণাবেক্ষণের সক্ষমতার বিষয়ে আদেশ প্রদান করিবে।\r\n'),
(24, 'মানসিক স্বাস্থ্য আইন, ২০১৮ (২০১৮ সনের ৬০ নং আইন)\r\n\r\nমানসিক অবস্থার বিচারিক অনুসন্ধান-ধারা ২০', 'উপ-ধারা (৫) ', 'উপ-ধারা (৪) এর অধীন প্রদত্ত আদেশের বিরুদ্ধে সংক্ষুব্ধ হইলে উচ্চতর আদালতে আপিল দায়ের করা যাইবে।\r\nব্যাখ্যা : এই ধারায় \'মানসিক অক্ষমতা (Mental disability)\' বলিতে কোনো ঘটনা প্রবাহ সম্পর্কে কোনো ব্যক্তির ধারণা লাভের\r\nঅসমর্থতাকে বুঝাইবে।\r\n\r\n');
