INSERT INTO `wild_animal` (`Id`, `Section`, `Sub-section`, `Details`) VALUES
(1, 'বন্যপ্রাণী ও উদ্ভিদ সংক্রান্ত নিষেধাজ্ঞা-ধারা ৬', 'উপ-ধারা (১)', 'এই আইনের অধীন লাইসেন্স বা ক্ষেত্রমত, পারমিট গ্রহণ ব্যতীত কোন ব্যক্তি কোন বন্যপ্রাণী শিকার বা তফসিল ৪ এ উল্লিখিত কোন উদ্ভিদ ইচ্ছাকৃতভাবে উঠানো, উপড়ানো, ধ্বংস বা সংগ্রহ করিতে পারিবেন না'),
(2, 'বন্যপ্রাণী ও উদ্ভিদ সংক্রান্ত নিষেধাজ্ঞা-ধারা ৬', 'উপ-ধারা (২) ', 'সরকার, সরকারি গেজেটে প্রজ্ঞাপন দ্বারা, কোন নির্দিষ্ট বা সকল বন্যপ্রাণী কোন নির্দিষ্ট বন বা এলাকা বা সমগ্র দেশে নির্দিষ্ট সময়ের জন্য শিকার নিষিদ্ধ করিতে পারিবে।'),
(3, 'বিপন্ন, বিপদাপন্ন ও মহা-বিপদাপন্ন প্রজাতি নির্ধারণ-ধারা ৭', '', 'বৈজ্ঞানিক উপাত্ত এবং আন্তর্জাতিক ভাবে গ্রহণযোগ্য বিধান বা প্রথা অনুসরণপূর্বক, প্রধান ওয়ার্ডেন, বৈজ্ঞানিক কমিটির সহিত পরামর্শক্রমে, তফসিল ১, ২ ও ৩ এ উল্লিখিত কোন কোন প্রজাতির বা উপ-প্রজাতির বন্যপ্রাণী বা তফসিল ৪ এ উল্লিখিত উদ্ভিদ বিপন্ন, বিপদাপন্ন বা মহা বিপদাপন্ন তাহা নির্ধারণ করিবেন।'),
(4, 'বন্যপ্রাণী অপসারণ,ইত্যাদি-ধারা ৮', 'উপ-ধারা (১) ', 'অন্য কোন আইনে ভিন্নরূপ কোন কিছু উল্লেখ না থাকিলে, কোন বন্যপ্রাণী\n(ক) মানুষের জীবন ও সম্পদের (গৃহপালিত পশু ও ফসলের) প্রতি হুমকির কারণ হইলে; অথবা\n(খ) দৈহিকভাবে পঙ্গু বা ছোঁয়াচে রোগে আক্রান্ত হইলে;অথবা\n(গ) কোন এলাকার প্রাকৃতিক ভারসাম্যের প্রতি হুমকির কারণ হইলে প্রধান ওয়ার্ডেন বা অতিরিক্ত প্রধান ওয়ার্ডেন বা ওয়ার্ডেন কারণ উল্লেখ করিয়া উক্ত বন্যপ্রাণী অপসারণ, হত্যা বা ক্ষেত্রমত, পুনর্বাসনের কার্যকর ব্যবস্থা গ্রহণ করিবেন এবং পনেরো দিনের মধ্যে বিষয়টি উপদেষ্টা বোর্ড ও বৈজ্ঞানিক কমিটিতে প্রতিবেদন\nআকারে উপস্থাপন করিবেন।\n'),
(5, 'বন্যপ্রাণী অপসারণ,ইত্যাদি-ধারা ৮', 'উপ-ধারা (২)', 'কোন বিমানঘাঁটি বা বিমান ক্ষেত্রে উড়োজাহাজের নিরাপত্তা নিশ্চিত করিবার উদ্দেশ্যে, কোন বন্যপ্রাণীকে অপসারণের ক্ষেত্রে এই ধারা প্রযোজ্য হইবে না।'),
(6, 'বন্যপ্রাণী অবমুক্তকরণ-ধারা ৯', '', 'অন্য কোন আইনে ভিন্নরূপ কিছু না থাকিলে, ধৃত, উদ্ধারকৃত বা জব্দকৃত কোন বণ্যপ্রাণী খাঁচায় বা আবদ্ধ অবস্থায় রাখা উহার জীবনের জন্য ঝুঁকিপুর্ণ হইলে উক্ত বন্যপ্রাণীকে উপযুক্ত প্রাকৃতিক পরিবেশ অবমুক্ত করিবার প্রয়োজনীয় ব্যবস্থা গ্রহণ করিতে হইবে।'),
(7, 'পারমিট প্রদান-ধারা ১০', '', 'কোন বন্যপ্রাণীর দেহের অংশ, মাংস, ট্রফি বা অসম্পূর্ণ ট্রফি সংগ্রহ এবং তফসিল ৪-এ উল্লিখিত উদ্ভিদ সংগ্রহ করা, দখলে রাখা, অথবা উহা হইতে উৎপাদিত দ্রব্য কোন বন অথবা দেশের যে কোন স্থান হইতে পরিবহনের জন্য নিম্নবর্ণিত কারণে প্রধান ওয়ার্ডেন বিধি দ্বারা নির্ধারিত পদ্ধতিতে এবং ফি প্রদান সাপেক্ষে পারমিট প্রদান করিতে পারিবেন, যথা :\n(ক) শিক্ষা;\n(খ) বৈজ্ঞানিক গবেষণা;\n(গ) বৈজ্ঞানিক ব্যবস্থাপনা;\n(ঘ) কোন উদ্ভিদ উদ্যান, সাফারী পার্ক, স্বীকৃত চিড়িয়াখানা, জাদুঘর, হার্বেরিয়াম অথবা একইরূপ অন্য কোন প্রতিষ্ঠানের জন্য নমুনা সংগ্রহ, সংরক্ষণ ও প্রদর্শন;\n(ঙ) জীবন রক্ষাকারী ঔষধ তৈরির জন্য উদ্ভিদ সাপের বিষ সংগ্রহ সংরক্ষণ;এবং\n(চ) সরকার কর্তৃক অনুমোদিত কোন ব্যক্তি বা প্রতিষ্ঠান কর্তৃক বংশ বিস্তারের জন্য।\nব্যাখ্যা।-এই ধারায় “বৈজ্ঞানিক ব্যবস্থাপনা” অর্থ\n(১) কোন বন্যপ্রাণীকে অন্য কোন সুবিধাজনক আবাসস্থলে স্থানান্তর করা;\n(২) কোন বন্যপ্রাণী অথবা নির্দিষ্ট প্রজাতির উদ্ভিদের ব্যবস্থাপনার জন্য হত্যা না করিয়া বা বিষ প্রয়োগ: করিয়া অথবা ধ্বংস না করিয়া প্রজনন নিয়ন্ত্রন করা; এবং\n(৩) বিজ্ঞানসম্মতভাবে কালিং (Cullin'),
(8, 'বন্যপ্রাণী ও উদ্ভিদ নিবন্ধিকরণ এবং নিবন্ধন সনদ ইস্যু-ধারা ১১', 'উপ-ধারা (১)', 'এই আইন কার্যকর হইবার একশত আশি দিনের মধ্যে প্রত্যেক ওয়ার্ডেন তাহার এলাকাধীন কোন ব্যক্তির নিকট সংগৃহীত ও সংরক্ষিত কোন বন্যপ্রাণী অথবা বন্যপ্রাণীদের অংশ, ট্রফি, অসম্পূর্ণ ট্রফি, অথবা তফসিল ৪ এ উল্লিখিত কোন উদ্ভিদ বা উহার অংশ বা উহা হইতে উৎপাদিত দ্রব্য নিবন্ধন করাইবেন, যথাযথ নিবন্ধনকরণ চিহ্ন প্রদান করিবেন এবং উহার সংখ্যা ও অবস্থান উল্লেখপূর্বক বিস্তারিত বিবরণ প্রধান ওয়ার্ডেন এর নিকট প্রতিবেদন আকারে উপস্থাপন করিবেনঃ\r\nতবে শর্ত থাকে যে, প্রথাগতভাবে পূর্ব হইতেই কোন ব্যক্তির হেফাজতে কোন ট্রফি বা বন্যপ্রাণীর স্মৃতি চিহ্ন থাকিলে এই ধারার বিধান প্রযোজ্য হইবে নাঃ\r\nতবে আরো শর্ত থাকে যে, উক্ত ব্যক্তির নিকট রক্ষিত কোন ট্রফি বা বন্যপ্রাণীর স্মৃতি চিহ্ন থাকিলে তিনি উহা প্রধান ওয়ার্ডেন বা ওয়ার্ডেন বা উপজেলা ফরেস্ট অফিসার এর নিকট ঘোষণা করিবেন।\r\n'),
(9, 'বন্যপ্রাণী ও উদ্ভিদ নিবন্ধিকরণ এবং নিবন্ধন সনদ ইস্যু-ধারা ১১\r\n', 'উপ-ধারা (২)  \r\n', 'উপ-ধারা (১) এর অধীন নিবন্ধিকরণ চিহ্ন প্রদানের পর উক্ত কর্মকর্তা বিধি ধারা নির্ধারিত পদ্ধতিতে উক্ত বন্যপ্রাণী, মাংস, ট্রফি, বা ক্ষেত্রমত, উদ্ভিদের আইনানুগ দখলের প্রমাণ স্বরূপ নিবন্ধন সনদ ইস্যু করিবেনঃ\r\nতবে শর্ত থাকে যে, লালন-পালনযোগ্য বন্যপ্রাণীর জন্য নিবন্ধন সনদ ইস্যুর পূর্বে নিশ্চিত হইতে হইবে যে, আবেদনকারী আর্থিকভাবে সচ্ছল এবং উক্ত বন্যপ্রাণী লালন-পালনের জন্য ক্ষেত্রমত, প্রয়োজনীয় স্থান, জলাধার, পরিবেশ, ফিডিং স্পট ও পরিচর্যাকারী লোকবল, লালন-পালন সম্পর্কে জ্ঞান ও সুযোগ সুবিধা বিদ্যমান রহিয়াছে।\r\n'),
(10, 'হস্তান্তর-ধারা ১২\r\n', 'উপ-ধারা (১) ', 'নিবন্ধন সনদ ব্যতীত কোন ব্যক্তি কোন বন্যপ্রাণী, মাংস, ট্রফি, অসম্পূর্ণ ট্রফি, বন্যপ্রাণীর অংশবিশেষ (ভারমিন ছাড়া) অথবা তফসিল ৪ এ উল্লিখিত উদ্ভিদ বা উহার অংশ বা উহা হইতে উৎপন্ন দ্রব্য দান, বিক্রয় বা অন্য কোন প্রকারে অন্য কোন ব্যক্তির নিকট হস্তান্তর করিতে পারিবেন না।\r\n'),
(11, 'হস্তান্তর-ধারা ১২', 'উপ-ধারা (২)', 'প্রধান ওয়ার্ডেন বা ক্ষেত্রমত, ওয়ার্ডেন এর পূর্বানুমোদন গ্রহণ করিয়া নিবন্ধন সনদপ্রাপ্ত কোন ব্যক্তি তাহার দখলে বা নিয়ন্ত্রণে বা তত্ত্বাবধানে রহিয়াছে এমন কোন বন্যপ্রাণী বা উহার অংশ, ট্রফি, অসম্পূর্ণ ট্রফি, অথবা তফসিল ৪-এ উল্লিখিত কোন উদ্ভিদ বা উহার অংশ বা উহা হইতে উৎপন্ন দ্রব্য কোন ব্যক্তি বা প্রতিষ্ঠান বা অন্য কাহারো নিকট, স্থানান্তর, হস্তান্তর বা পরিবহণ করিতে পারিবেন না।'),
(12, 'হস্তান্তর-ধারা ১২\r\n', 'উপ-ধারা (৩)', 'চিড়িয়াখানার আবদ্ধ প্রাণী বিনিময়ের ক্ষেত্রে এই ধারার বিধান প্রযোজ্য হইবে না।'),
(13, 'লাইসেন্স-ধারা ২৪', 'উপ-ধারা (১) ', 'কোন ব্যক্তি কোন বন্যপ্রাণী অথবা উহার দেহের অংশ, মাংস, ট্রফি, অসম্পূর্ণ ট্রফি বা তফসিল ৪-এ উল্লিখিত কোন উদ্ভিদ বাণিজ্যিক উদ্দেশ্যে চাষ, আহরণ, উৎপাদন, লালন-পালন, আমদানি-রপ্তানি অথবা কোন বন্যপ্রাণী শিকার করিতে চাহিলে, প্রধান ওয়ার্ডেন বা তৎকর্তৃক ক্ষমতাপ্রাপ্ত কোন কর্মকর্তার নিকট হইতে বিধি দ্বারা নির্ধারিত পদ্ধতিতে এবং ফি প্রদান সাপেক্ষে লাইসেন্স গ্রহণ করিতে হইবে।\r\n'),
(14, 'লাইসেন্স-ধারা ২৪', 'উপ-ধারা (২)', 'এই আইন কার্যকর হইবার পূর্বে কোন ব্যক্তি কোন বন্যপ্রাণী অথবা উহার দেহের অংশ, মাংস, ট্রফি, অসম্পূর্ণ ট্রফি বা তফসিল ৪ এ উল্লিখিত কোন উদ্ভিদ বাণিজ্যিক উদ্দেশ্যে উৎপাদনপূর্বক মত করিয়া রাখিলে এই আইন কার্যকর হইবার ৬০ (ষাট) দিনের মধ্যে উপ-ধারা (১) এর বিধান অনুযায়ী লাইসেন্স গ্রহণ করিতে হইবে।'),
(15, 'লাইসেন্স-ধারা ২৪', 'উপ-ধারা (৩)', 'উপ-ধারা (২) এর অধীন আবেদনের প্রেক্ষিতে প্রধান ওয়ার্ডেন বা তৎকর্তৃক ক্ষমতাপ্রাপ্ত কোন কর্মকর্তা যথাযথ পরিদর্শন ও পরীক্ষা-নিরীক্ষা করিয়া সন্তুষ্ট হইলে মজুতকৃত বন্যপ্রাণী অথবা উহার দেহের অংশ, মাংস, ট্রফি, অসম্পূর্ণ ট্রফি বা তফসিল ৪-এ উল্লিখিত উদ্ভিদে সনাক্তকরণ চিহ্ন প্রদান করিবেন এবং বিধি দ্বারা নির্ধারিত পদ্ধতিতে লাইসেন্স প্রদান করিবেন।'),
(16, 'লাইসেন্স-ধারা ২৪\r\n', 'উপ-ধারা (৪)', 'লাইসেন্সের মেয়াদ প্রদানের তারিখ হইতে ১ (এক) বৎসর পর্যন্ত বহাল থাকিবে।'),
(17, 'লাইসেন্স-ধারা ২৪', 'উপ-ধারা (৫)', 'বিধি দ্বারা নির্ধারিত পদ্ধতিতে এবং ফি প্রদান সাপেক্ষে লাইসেন্স নবায়ন করিতে হইবে।'),
(18, 'আমদানি-ধারা ২৮', 'উপ-ধারা (১)', 'কোন ব্যক্তি-\r\n(ক) আগমন শুল্ক বন্দর ব্যতীত অন্য কোন পথে;\r\n(খ) প্রযোজ্য ক্ষেত্রে সাইটিস (CITES) সাটিফিকেট ব্যতীত; এবং\r\n(গ) লাইসেন্স ব্যতীত কোন বন্যপ্রাণী বা উহার অংশ, ট্রফি, অসম্পূর্ণ ট্রফি, অথবা তফসিল ৪-এ উল্লিখিত উদ্ভিদ বা উহার অংশ বা উহা হইতে উৎপন্ন দ্রব্য বাংলাদেশে আমদানি করিতে পারিবেন না।\r\n'),
(19, 'আমদানি-ধারা ২৮', 'উপ-ধারা (২)', 'প্রতিটি আমদানি করা বন্যপ্রাণী বা তফসিল ৪-এ উল্লিখিত উদ্ভিদ অথবা উহা হইতে উৎপন্ন দ্রব্য আগমন শুল্ক বন্দরে আসার পর আমদানিকৃত রাষ্ট্রের উপযুক্ত কর্তৃপক্ষ কর্তৃক ইস্যুকৃত কোয়ারেন্টাইন সার্টিফিকেট প্রদর্শন করিতে হইবে।'),
(20, 'রপ্তানি-ধারা ২৯', '', 'কোন ব্যক্তি -\r\n(ক) বহির্গমন শুল্ক বন্দর ব্যতীত অন্য কোন পথে;\r\n(খ) প্রযোজ্য ক্ষেত্রে, সাইটিস (CITES) সার্টিফিকেট ব্যতীত; এবং \r\n(গ) লাইসেন্স ব্যতীত কোন বন্যপ্রাণী বা উহার অংশ, ট্রফি, অসম্পূর্ণ ট্রফি, অথবা তফসিল ৪-এ উল্লিখিত উদ্ভিদ বা উহার অংশ বা উহা হইতে উৎপন্ন দ্রব্য রপ্তানি বা পুনঃরপ্তানি করিতে পারিবেন না।\r\n'),
(21, 'বন্যপ্রাণী অপরাধ নিয়ন্ত্রণ ইউনিট গঠন-ধারা ৩১', 'উপ-ধারা (১)', 'সরকার, সরকারি গেজেটে প্রজ্ঞাপন দ্বারা, বন্যপ্রাণী সংক্রান্ত আন্তর্জাতিক কনভেনশন, প্রটোকল চুক্তি ইত্যাদির সঠিক প্রয়োগ ও বাস্তবায়ন নিশ্চিত করিবার জন্য বাংলাদেশের গুরুত্বপূর্ণ বিমান বন্দর, স্থল বন্দর বা সমুদ্র বন্দরসহ বাংলাদেশের যে কোন স্থানে শুল্ক কর্মকর্তা, আইনশৃঙ্খলা রক্ষাকারী বাহিনীর সদস্য সমন্বয়ে বন্যপ্রাণী অপরাধ নিয়ন্ত্রণ ইউনিট গঠন করিতে পারিবে।\r\n'),
(22, 'বন্যপ্রাণী অপরাধ নিয়ন্ত্রণ ইউনিট গঠন-ধারা ৩১\r\n', 'উপ-ধারা (২)', 'উপ-ধারা (১) এ উল্লিখিত বন্যপ্রাণী অপরাধ নিয়ন্ত্রণ ইউনিটের গঠন, ক্ষমতা ও কার্যাবলী, সরকার বিধি দ্বারা নির্ধারণ করিতে পারিবে।'),
(23, 'জব্দকরণ-ধারা ৩২\r\n', 'উপ-ধারা (১)', 'উপ-ধারা (১) কোন কর্মকর্তা এই আইনের অধীন ব্যবস্থা গ্রহণের নিমিত্ত নিম্নবর্ণিত দ্রব্য বা সামগ্রী জব্দ করিতে পারিবেন, যথাঃ\r\n(ক) লাইসেন্স ব্যতীত শিকার করা, দখলে রাখা বা ধৃত বন্যপ্রাণী বা উহাদের আবদ্ধ অবস্থায় রাখিবার কারণে প্রজননের মাধ্যমে বংশ বৃদ্ধিপ্রাপ্ত প্রাণী;\r\n(খ) দুর্ঘটনার কারণে মারা গিয়াছে বা মৃতপ্রায় অবস্থায় রহিয়াছে এইরূপ বন্যপ্ৰাণী:\r\n(গ) এই আইন অনুযায়ী নিবন্ধিত অথবা লাইসেন্স গ্রহণ করা হয় নাই এইরূপ কোন বন্যপ্রাণী বা উহার কোন অংশ, ট্রফি, অসম্পূর্ণ ট্রফি, মাংস, দেহের অংশ অথবা তফসিল ৪-এ উল্লিখিত উদ্ভিদ বা উহার অংশ বা উহা হইতে উৎপন্ন দ্রব্য;\r\n(ঘ) অপরাধ সংঘটনে ব্যবহৃত অস্ত্র, বস্তু ও যন্ত্রপাতি;\r\n(ঙ) ধারা ২৮ ও ২৯ অনুযায়ী আমদানি বা রপ্তানি করা হয় নাই এমন কোন বন্যপ্রাণী বা উহার অংশ, ট্রফি, অসম্পূর্ণ ট্রফি, মাংস, দেহের অংশ অথবা তফসিল ৪ এ উল্লিখিত উদ্ভিদ বা উহার অংশ বা উহা হইতে উৎপন্ন দ্রব্য:\r\nতবে শর্ত থাকে যে, ক্ষুদ্র নৃ-গোষ্ঠীর প্রথাগত, ঐতিহ্য বা দৈনন্দিন জীবন ধারণের অংশ হিসাবে ব্যবহৃত বন্যপ্রাণীর ট্রফি বা স্মৃতি চিহ্ন এর ক্ষেত্রে এই উপ-ধারার বিধান প্রযোজ্য হইবে না।\r\n'),
(24, 'জব্দকরণ-ধারা ৩২', 'উপ-ধারা (২)', 'উপ-ধারা (১) এর অধীন জব্দকৃত সকল দ্রব্য বা সামগ্রী সরকারের অনুকূলে বাজেয়াপ্তযোগ্য হইবে।\r\n'),
(25, 'জব্দকরণ-ধারা ৩২', 'উপ-ধারা (৩)', 'উপ-ধারা (১) এর অধীন জব্দকৃত যে সকল দ্রব্য বা সামগ্রী দ্রুত এবং প্রাকৃতিকভাবে পঁচনশীল সে সকল দ্রব্য বা সামগ্রী জব্দকারী কর্মকর্তা বিধি দ্বারা নির্ধারিত পদ্ধতিতে বিক্রয়, ধ্বংস, অপসারণ বা অন্য কোন পন্থায় নিষ্পত্তির ব্যবস্থা গ্রহণ করিবেন।'),
(26, 'প্রবেশ, ইত্যাদির ক্ষমতা-ধারা ৩৩', 'উপ-ধারা (১)', 'এই আইন বা বিধির বিধানাবলী যথাযথভাবে পালিত হইতেছে কিনা তাহা যাচাইয়ের জন্য প্রধান ওয়ার্ডেন বা তৎকর্তৃক ক্ষমতাপ্রাপ্ত অন্য কোন কর্মকর্তা যে কোন সময় যে কোন স্থানে প্রবেশ,তল্লাশি বা কোন কিছু আটক বা কোন কিছুর নমুনা সংগ্রহ,পরীক্ষা-নিরীক্ষা বা কোন স্থান পরিদর্শন করিতে পারিবেন।\r\n'),
(27, 'প্রবেশ, ইত্যাদির ক্ষমতা-ধারা ৩৩', ' উপ-ধারা (২)', 'এই ধারার অধীন তল্লাশি,আটক বা পরিদর্শনের ক্ষেত্রে,এই আইনের বিধানাবলীর সহিত অসঙ্গতিপূর্ণ না হওয়া সাপেক্ষে,ফৌজদারী কার্যবিধি প্রযোজ্য হইবে।'),
(28, 'প্রবেশ, ইত্যাদির ক্ষমতা-ধারা ৩৩', 'উপ-ধারা (৩)', 'বনজদ্রব্যের সকল ক্রেতা, সহ-ব্যবস্থাপনা পদ্ধতির সহিত জড়িত ব্যক্তি, আইন শৃঙ্খলা রক্ষাকারী বাহিনীর সদস্য, গণপূর্ত বিভাগ ও কৃষি সম্প্রসারণ বিভাগে কর্মরত ব্যক্তি, চৌকিদার, দফাদার, গ্রাম প্রতিরক্ষা দলের সদস্য, ভিলেজ হেডম্যান, ইউনিয়ন পরিষদের চেয়ারম্যান ও সদস্য, কানুনগো এবং ইউনিয়ন ভূমি সহকারী এই আইন বা বিধির অধীন দায়িত্ব সম্পাদনের জন্য ক্ষমতাপ্রাপ্ত ব্যক্তিকে সকল সহযোগিতা প্রদান করিতে বাধ্য থাকিবেন।\r\n\r\n'),
(29, 'কতিপয় অপরাধের দণ্ড-ধারা ৩৪', '', 'কোন ব্যক্তি যদি-\r\n(ক) ধারা ১১ এর বিধান অনুযায়ী নিবন্ধিত এবং প্রদত্ত নিবন্ধন চিহ্ন নকল, বিনিময় অথবা অন্য কোনভাবে হস্তক্ষেপ বা পরিবর্তন করেন; বা\r\n(খ) লাইসেন্স অথবা পারমিট প্রাপ্ত কোন ব্যক্তি ব্যতীত অন্য কাহারো নিকট হইতে কোন বন্যপ্রাণী, বন্যপ্রাণীর কোন অংশ, মাংস, ট্রফি অথবা উহা হইতে উৎপন্ন দ্রব্য বা বনজদ্রব্য বা তফসিল ৪-এ উল্লিখিত উদ্ভিদ অথবা উহা হইতে উৎপন্ন দ্রব্যাদি ক্রয়-বিক্রয় বা আমদানি-রপ্তানি করেন তাহা হইলে তিনি অপরাধ করিয়াছেন বলিয়া গণ্য হইবেন এবং উক্ত অপরাধের জন্য তিনি সর্বোচ্চ ১ (এক) বৎসর পর্যন্ত কারাদণ্ড অথবা সর্বোচ্চ ৫০ (পঞ্চাশ) হাজার টাকা পর্যন্ত অর্থদণ্ড অথবা উভয় দণ্ডে দণ্ডিত হইবেন এবং একই অপরাধের পুনরাবৃত্তি ঘটাইলে সর্বোচ্চ ৩ (তিন) বৎসর পর্যন্ত কারাদণ্ড অথবা সর্বোচ্চ ২ (দুই) লক্ষ টাকা পর্যন্ত অর্থদণ্ড বা উভয় দণ্ডে দণ্ডিত হইবেন।\r\n'),
(30, 'বাঘ ও হাতি হত্যা, ইত্যাদির দণ্ড–ধারা ৩৬', 'উপ-ধারা (১)', ' কোন ব্যক্তি ধারা ২৪ এর অধীন লাইসেন্স গ্রহণ না করিয়া তফসিল ১-এ উল্লিখিত কোন বাঘ বা হাতি হত্যা করিলে তিনি অপরাধ করিয়াছেন বলিয়া গণ্য হইবেন ও উক্তরূপ অপরাধের জন্য জামিন অযোগ্য হইবেন এবং তিনি সর্বনিম্ন ২ (দুই) বৎসর এবং সর্বোচ্চ ৭ (সাত) বৎসর পর্যন্ত কারাদণ্ড এবং সর্বনিম্ন ১ (এক) লক্ষ এবং সর্বোচ্চ ১০ (দশ) লক্ষ টাকা পর্যন্ত অর্থদণ্ডে দণ্ডিত হইবেন এবং একই অপরাধের পুনরাবৃত্তি ঘটাইলে সর্বোচ্চ ১২ (বার) বৎসর পর্যন্ত কারাদণ্ড এবং সর্বোচ্চ ১৫(পনের) লক্ষ টাকা পর্যন্ত অর্থদণ্ডে দণ্ডিত হইবেনঃ\r\nতবে শর্ত থাকে যে, বাঘ বা হাতি কর্তৃক কোন ব্যক্তি আক্রান্ত হইলে এবং উহার ফলে তাহার জীবনাশঙ্কার সৃষ্টি হইলে জীবন রক্ষার্থে উক্ত আক্রমণকারী বাঘ বা হাতিকে হত্যার ক্ষেত্রে এই ধারার বিধান প্রযোজ্য হইবে নাঃ\r\nতবে আরো শর্ত থাকে যে, এ সংক্রান্ত বিষয়ে কোন মামলা দায়েরের প্রশ্ন দেখা দিলে, সংশ্লিষ্ট স্টেশন কর্মকর্তা ওয়ার্ডেন এর সহিত পরামর্শক্রমে মামলা দায়ের করিতে পারিবেন।\r\n\r\n'),
(31, 'বাঘ ও হাতি হত্যা, ইত্যাদির দণ্ড–ধারা ৩৬', 'উপ-ধারা (২)', 'কোন ব্যক্তি ধারা ১০-এর অধীন পারমিট গ্রহণ না করিয়া তফসিল ১ এ উল্লিখিত কোন বাঘ বা হাতির ট্রফি, অসম্পূর্ণ ট্রফি, মাংস, দেহের অংশ সংগ্রহ করিলে, দখলে রাখিলে বা ক্রয় বা বিক্রয় করিলে বা পরিবহন করিলে তিনি অপরাধ করিয়াছেন বলিয়া গণ্য হইবেন এবং উক্তরূপ অপরাধের জন্য তিনি সর্বোচ্চ৩(তিন)বৎসর পর্যন্ত কারাদণ্ড অথবা সর্বোচ্চ ৩(তিন)লক্ষ টাকা পর্যন্ত অর্থদণ্ড অথবা উভয় দণ্ডে দণ্ডিত হইবেন এবং একই অপরাধের পুনরাবৃত্তি ঘটাইলে সর্বোচ্চ ৫(পাঁচ)বৎসর পর্যন্ত কারাদণ্ড অথবা ৫(পাঁচ)লক্ষ টাকা অর্থদণ্ড বা উভয় দণ্ডে দণ্ডিত হইবেন।\r\n'),
(32, 'চিতা বাঘ, লাম চিতা, উল্লুক, সাম্বার হরিণ, কুমির, ঘড়িয়াল, তিমি বা ডলফিন হত্যা, ইত্যাদির দণ্ড-ধারা ৩', 'উপ-ধারা (১)', 'কোন ব্যক্তি তফসিল ১-এ উল্লিখিত কোন চিতা বাঘ, লাম চিতা, উল্লুক, সাম্বার হরিণ, কুমির, ঘড়িয়াল, তিমি বা ডলফিন হত্যা করিলে তিনি অপরাধ করিয়াছেন বলিয়া গণ্য হইবেন এবং উক্তরূপ অপরাধের জন্য তিনি সর্বোচ্চ ৩(তিন)বৎসর পর্যন্ত কারাদণ্ড অথবা সর্বোচ্চ ৩(তিন) লক্ষ টাকা পর্যন্ত অর্থদণ্ড অথবা উভয় দণ্ডে দণ্ডিত হইবেন এবং একই অপরাধের পুনরাবৃত্তি ঘটাইলে সর্বোচ্চ ৫(পাঁচ)বৎসর পর্যন্ত কারাদণ্ড অথবা সর্বোচ্চ ৫(পাঁচ) লক্ষ টাকা অর্থদণ্ড বা উভয় দণ্ডে দণ্ডিত হইবেনঃ\r\nতবে শর্ত থাকে যে, চিতা বাঘ বা কুমির কর্তৃক কোন ব্যক্তি আক্রান্ত হইলে এবং উহার ফলে তাহার জীবনাশঙ্কার সৃষ্টি হইলে জীবন রক্ষার্থে উক্ত আক্রমণকারী চিতা বাঘ বা কুমিরকে হত্যার ক্ষেত্রে এই ধারার বিধান প্রযোজ্য হইবে নাঃ তবে আরো শর্ত থাকে যে, এ সংক্রান্ত বিষয়ে কোন মামলা দায়েরের প্রশ্ন দেখা দিলে, সংশ্লিষ্ট স্টেশন কর্মকর্তা ওয়ার্ডেন এর সহিত পরামর্শক্রমে মামলা দায়ের করিতে পারিবেন।\r\n'),
(33, 'চিতা বাঘ, লাম চিতা, উল্লুক, সাম্বার হরিণ, কুমির, ঘড়িয়াল, তিমি বা ডলফিন হত্যা, ইত্যাদির দণ্ড-ধারা ৩', 'উপ-ধারা (২) ', 'কোন ব্যক্তি তফসিল ১-এ উল্লিখিত কোন চিতা বাঘ, লাম চিতা, উল্লুক, সাম্বার হরিণ, কুমির, ঘড়িয়াল, তিমি বা ডলফিন এর ট্রফি বা অসম্পূর্ণ ট্রফি, মাংস, দেহের অংশ সংগ্রহ করিলে, দখলে রাখিলে বা ক্রয় বা বিক্রয় করিলে বা পরিবহন করিলে তিনি অপরাধ করিয়াছেন বলিয়া গণ্য হইবেন এবং উক্তরূপ অপরাধের জন্য তিনি সর্বোচ্চ ২(দুই)বৎসর পর্যন্ত কারাদণ্ড অথবা সর্বোচ্চ ১(এক)লক্ষ টাকা পর্যন্ত অর্থদণ্ড অথবা উভয় দণ্ডে দণ্ডিত হইবেন এবং একই অপরাধের পুনরাবৃত্তি ঘটাইলে সর্বোচ্চ ৪(চার)বৎসর পর্যন্ত কারাদণ্ড অথবা সর্বোচ্চ ২ (দুই)লক্ষ টাকা অর্থদণ্ড বা উভয় দণ্ডে দণ্ডিত হইবেন।'),
(34, 'পাখি বা পরিযায়ী পাখি হত্যা,ইত্যাদির দণ্ড-ধারা ৩৮', 'উপ-ধারা (১) ', 'কোন ব্যক্তি তফসিল ১ ও ২ এ উল্লিখিত কোন পাখি বা পরিযায়ী পাখি হত্যা করিলে তিনি অপরাধ করিয়াছেন বলিয়া গণ্য হইবেন এবং উক্তরূপ অপরাধের জন্য তিনি সর্বোচ্চ ১(এক)বৎসর পর্যন্ত কারাদণ্ড অথবা সর্বোচ্চ ১(এক)লক্ষ টাকা পর্যন্ত অর্থদণ্ড অথবা উভয় দণ্ডে দণ্ডিত হইবেন এবং একই অপরাধের পুনরাবৃত্তি ঘটাইলে সর্বোচ্চ ২(দুই)বৎসর পর্যন্ত কারাদণ্ড অথবা সর্বোচ্চ ২(দুই)লক্ষ টাকা অর্থদণ্ড বা উভয় দণ্ডে দণ্ডিত হইবেন।\r\n'),
(35, 'পাখি বা পরিযায়ী পাখি হত্যা,ইত্যাদির দণ্ড-ধারা ৩৮', 'উপ-ধারা (২) \r\n', 'কোন ব্যক্তি তফসিল ১ ও ২ এ উল্লিখিত কোন পাখি বা পরিযায়ী পাখির ট্রফি বা অসম্পূর্ণ ট্রফি, মাংস, দেহের অংশ সংগ্রহ করিলে, দখলে রাখিলে বা ক্রয় বা বিক্রয় করিলে বা পরিবহন করিলে তিনি অপরাধ করিয়াছেন বলিয়া গণ্য হইবেন এবং উক্তরূপ অপরাধের জন্য তিনি সর্বোচ্চ ৬(ছয়)মাস পর্যন্ত কারাদণ্ড অথবা সর্বোচ্চ ৩০ (ত্রিশ)হাজার টাকা পর্যন্ত অর্থদণ্ড অথবা উভয় দণ্ডে দণ্ডিত হইবেন এবং একই অপরাধের পুনরাবৃত্তি ঘটাইলে সর্বোচ্চ ১(এক)বৎসর পর্যন্ত কারাদণ্ড অথবা সর্বোচ্চ ৫০(পঞ্চাশ)হাজার টাকা অর্থদণ্ড বা উভয় দণ্ডে দণ্ডিত হইবেন।\r\n'),
(36, 'ধারা ৬, ১০, ১১ ও ১২ এর বিধান লংঘনের দণ্ড-ধারা ৩৯\r\n', '', 'কোন ব্যক্তি ধারা ৬, ১০, ১১ বা ১২ এর বিধান লংঘন করিলে তিনি অপরাধ করিয়াছেন বলিয়া গণ্য হইবেন এবং উক্তরূপ অপরাধের জন্য তিনি সর্বোচ্চ ১(এক)বৎসর পর্যন্ত কারাদণ্ড অথবা সর্বোচ্চ ৫০(পঞ্চাশ)হাজার টাকা পর্যন্ত অর্থদণ্ড অথবা উভয় দণ্ডে দণ্ডিত হইবেন এবং একই অপরাধের পুনরাবৃত্তি ঘটাইলে সর্বোচ্চ ২(দুই)বৎসর পর্যন্ত কারাদণ্ড অথবা সর্বোচ্চ ১(এক)লক্ষ টাকা অর্থদণ্ড বা উভয় দণ্ডে দণ্ডিত হইবেন।'),
(37, 'অপরাধ সংঘটনের সহায়তা, প্ররোচনা, ইত্যাদির দণ্ড-ধারা ৪১', '', 'কোন ব্যক্তি এই আইনের অধীন কোন অপরাধ সংঘটনে প্রত্যক্ষ বা পরোক্ষভাবে সহায়তা করিলে বা উক্ত অপরাধ সংঘটনে প্ররোচনা প্রদান করিলে এবং উক্ত সহায়তা বা প্ররোচনার ফলে অপরাধটি সংঘটিত হইলে, উক্ত সহায়তাকারী বা প্ররোচনাকারী তাহার সহায়তা বা প্ররোচনা দ্বারা সংঘটিত অপরাধের জন্য নির্ধারিত দণ্ডে দণ্ডিত হইবেন।\r\n'),
(38, 'অপরাধের আমলযোগ্যতা,আমল অযোগ্যতা,জামিনযোগ্যতা,জামিন অযোগ্যতা ও আপোষ যোগ্যতা-ধারা ৪৩', '\r\n', 'ধারা ৩৬-এর অধীন সংঘটিত অপরাধ আমলযোগ্য ও অজামিনযোগ্য হইবে এবং উক্ত ধারা ব্যতীত অন্যান্য ধারার অধীনে সংঘটিত অপরাধ আমল অযোগ্য, জামিনযোগ্য ও ক্ষতিপূরণ সাপেক্ষে আপোষযোগ্য।'),
(39, 'অপরাধ বিচারার্থ গ্রহণ ও বিচার-ধারা 88', 'উপ-ধারা (১)', 'ধারা ৪৩-এর বিধান সাপেক্ষে, এই আইনের অধীন ক্ষমতাপ্রাপ্ত কোন কর্মকর্তা অথবা ক্ষতিগ্রস্ত কোন ব্যক্তির লিখিত অভিযোগ ব্যতিরেকে কোন আদালত এই আইনের অধীন সংঘটিত কোন অপরাধ বিচারার্থ গ্রহণ করিবে না।'),
(40, 'অপরাধ বিচারার্থ গ্রহণ ও বিচার-ধারা 88', 'উপ-ধারা (২) \r\n', 'Code of Criminal Procedure, 1898 (Act V of 1898) বা অন্য কোন আইনে যাহা কিছুই থাকুক না কেন, এই আইনের অধীন সংঘটিত অপরাধসমূহের বিচার প্রথম শ্রেণীর জুডিসিয়াল ম্যাজিস্ট্রেট, বা, ক্ষেত্রমত, মেট্রোপলিটন ম্যাজিস্ট্রেট কর্তৃক বিচার্য হইবেঃ\r\nতবে শর্ত থাকে যে, এই আইনের উদ্দেশ্য পূরণকল্পে, ক্ষেত্রমত, Code of Criminal Procedure, 1898 এর section 12 এর অধীন ক্ষমতাপ্রাপ্ত কোন স্পেশাল ম্যাজিস্ট্রেট বা স্পেশাল মেট্রোপলিটন ম্যাজিস্ট্রেট কর্তৃক এই আইনের অধীন সংঘটিত অপরাধসমূহ বিচার্য হইবেঃ\r\n'),
(41, 'অপরাধ বিচারার্থ গ্রহণ ও বিচার-ধারা 88', 'উপ-ধারা (৩)', 'উপ-ধারা (২)এবং Code of Criminal Procedure, 1898 এ যাহা কিছুই থাকুক না কেন, ধারা ৩৬ এ উল্লিখিত অপরাধের বিচার Courts of Sessions কর্তৃক বিচার্য হইবে।'),
(42, 'অপরাধ বিচারার্থ গ্রহণ ও বিচার-ধারা 88', 'উপ-ধারা (8) ', 'Code of Criminal Procedure, 1898 এ যাহা কিছুই থাকুক না কেন, এই আইনের অধীন কোন প্রথম শ্রেণীর জুডিসিয়াল ম্যাজিস্ট্রেট বা মেট্রোপলিটন ম্যাজিস্ট্রেট দোষী সাব্যস্ত ব্যক্তিকে সংশ্লিষ্ট অপরাধের জন্য এই আইনে অনুমোদিত যে কোন অর্থদণ্ড আরোপ করিতে পারিবে।\r\n'),
(43, 'ফৌজদারী কার্যবিধির প্রয়োগ-ধারা ৪৫\r\n', '', 'এই আইনের বিধানাবলীর সহিত অসঙ্গতিপূর্ণ না হওয়া সাপেক্ষে, এই আইনের অধীন অপরাধের তদন্ত, বিচার, আপীল ও অন্যান্য সংশ্লিষ্ট বিষয়ে ফৌজদারী কার্যবিধি প্রযোজ্য হইবে।');
