$( document ).ready(function() {
    $('input:radio[name="confession_0"]').change(function(){  
         var radval = $('input[name="confession_0"]:checked').val();
         if(radval == 'Yes')
          {
           $(".confession_0").show();
           if($(".confession_0").val())
            $('#uploadFiles_0').prop('required',true);
           $('.copy_statement_0').prop('required',true);
           $('#date_0').prop('required',true);
           $('.divcopystatement0').show();
          }else{
              $(".confession_0").hide();
              $('#uploadFiles_0').prop('required',false);
              $('.copy_statement_0').prop('required',false);
              $('#date_0').prop('required',false);
              $('#copy_statement_0').prop('required',false);
              $('.copy_statement_0').prop('checked', false);
              $('.copy_statement_0').trigger('change');
              //$(".listinBtn").removeAttr("disabled");
          }
          rediobtn_checked();
         });
  });
